css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {
q{
background:#222;
color:#ddd;
padding:6px;
font-size:.9em;
line-height:1.5em!important;
}
q strong{
font-family:"Trebuchet MS";
padding:10px;
padding-bottom:20px;
text-align:center;
color:#5995D2;
}

.lit-bkmrk{
background:#222!important;
}
img.lit{
position:relative;
z-index:1400;
width:32px;
height:200px;
}
a.thumb.lit.journal img.lit{
display:none!important;
}
a.thumb.lit.journal q strong{
font-weight:normal;
letter-spacing:.1em!important;
font-family:'Trebuchet MS'!important;
color:#5995D2!important;
padding-bottom:15px!important;
}
a.thumb.lit.journal q{
font-size:.85em!important;
color:#ddd!important;
}
a.thumb.lit.journal .wrap{
background:#222!important;
}
.thumb.lit{
background:#222!important;
}

.deviation-visit .stream img.lit{
z-index:0!important;
width:12px;
height:75px;
}
.deviation-visit .stream q{
background:transparent!important;
color:#222;
margin-left:-12px;
}
.deviation-visit .thumb.lit{
margin-top:3px!important;
margin-left:2px!important;
}

.mczone img.lit, .gr-box img.lit, .journalbox img.lit, .text-ii img.lit, #footerThumbData.stream img.lit, #ddtable img.lit, .c.block.pppt img.lit{
max-width:20px!important;
max-height:125px!important;
}
}
