css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(https://userstyles.org/) {

body{
max-width:98% !important;
}

a{color:#fff !important;}
a:hover{text-shadow: rgba(255, 255, 255,0.5) 0px 0px 1px !important;}

html{
border-style:none !important;
text-shadow: rgba(0, 0, 0, 0.5) 1px 1px 0px !important;
color:#ccc !important;
background: url('http://i182.photobucket.com/albums/x146/Nihagi/lsbg.png') !important;
}

#left-sidebar{
background:#222 !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
padding-top:5px !important;
}

#left-sidebar nav, form#search, footer#related{
background: url('http://i182.photobucket.com/albums/x146/Nihagi/lsbg.png') !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px inset !important;
border-radius:0px !important;
}

#show-button{
background:url('http://i182.photobucket.com/albums/x146/Nihagi/lsbg.png') !important;
padding:1px 5px 1px 5px !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px inset !important;
}

li.more{
border-top:1px solid #333 !important;
}

#main-header{
background:#222 !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
border-radius:0px !important;
}

#screenshots{
background: url('http://i182.photobucket.com/albums/x146/Nihagi/lsbg.png') !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px inset !important;
border:0px !important;
border-radius:0px !important;
}

.listing-left-info{
padding:5px !important;
margin-right:10px !important;
background:#222 !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
border:0px !important;
border-radius:0px !important;
}

#social{
display:none!important;
}

.style-brief-text{
background:#222 !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
border:0px !important;
border-radius:0px !important;
margin:10px !important;
margin-right:0px !important;
margin-left:370px !important;
padding:5px !important;
}

section#obsoletion-message{
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px inset !important;
background:#712b2b !important;
position:absolute !important;
top:12px !important;
left:1.5% !important;
height:18px !important;
width:158px !important;
padding:10px !important;
padding-top:5px !important;
padding-left:20px !important;
border-radius:0px !important;
overflow:hidden !important;
}

textarea#obsoletion-message{
height:100px;
padding:5px !important;
}

#view-code{
background-color: rgba(255,255,255,0.1) !important;
text-shadow: rgba(0, 0, 0, 0.0) 0px 0px 0px !important;
color:#000 !important;
}

.install-status{
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px inset !important;
}

#switch-browser-note{
padding-top:10px !important;
}

#discussions-area{
}

input{
margin:2px !important;
}

#main-article{
background:#222 !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
border:0px !important;
border-radius:0px !important;
margin:10px !important;
margin-left:190px !important;
padding:10px !important;
overflow:auto !important;
}

.style-brief{
padding:10px !important;
background: url('http://i182.photobucket.com/albums/x146/Nihagi/lsbg.png') !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px inset !important;
}



input[type="radio"], input[type="radio"]:active{
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
border-style:none !important;
border:0px !important;
-moz-appearance:none !important;
-webkit-appearance:none !important; 
-appearance:none !important;
}

input[type="button"], article button, article input[type="submit"]{
background: url('http://i182.photobucket.com/albums/x146/Nihagi/lsbg.png') !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 0px 0px, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px inset !important;
border-style:none !important;
color: rgb(204, 204, 204);
text-shadow:rgba(0, 0, 0, 0.6) 0px 1px 0px;
border:0px solid #000 !important;
-moz-appearance:none !important;
-webkit-appearance:none !important; 
-appearance:none !important;
}

input, textarea, select{
color:rgb(204, 204, 204) !important;
border-style:none !important;
background:#1a1a1a !important;
box-shadow:rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
-moz-appearance:none !important;
-webkit-appearance:none !important; 
-appearance:none !important;
}

input[type="file"]{
box-shadow:none!important;
}

input[type="button"]:hover{
color: rgb(204, 204, 204);
text-shadow:rgba(0, 0, 0, 1.0) 0px 0px 3px;
box-shadow:none!important;
box-shadow: rgba(255, 255, 255, 0.3) 0px 0px 2px 0px, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px inset !important;
}

input[type="url"], input[type="url"]:active, input[type="text"], input[type="text"]:active{
border-style:none !important;
border:0px !important;
-moz-appearance:none !important;
-webkit-appearance:none !important; 
-appearance:none !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
}

input[type="text"]{
margin:4px !important;
border-style:none !important;
border:0px solid #000 !important;
-moz-appearance:none !important;
-webkit-appearance:none !important; 
-appearance:none !important;
}

#style-options select, #style-options input[type="url"], #style-options input[type="text"]{
border-style:none !important;
border:0px solid #000 !important;
-moz-appearance:none !important;
-webkit-appearance:none !important; 
-appearance:none !important;
margin-right:2px !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
margin-top:2px !important;
margin-bottom:2px !important;
position:absolute !important;
left:850px !important;
}

select{
border-style:none !important;
border:0px solid #000 !important;
-moz-appearance:none !important;
-webkit-appearance:none !important; 
-appearance:none !important;
margin-right:2px !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
margin-top:2px !important;
margin-bottom:2px !important;
}

#style-options li{
line-height:22px !important;
border-top:1px solid #333 !important;
}

#style-options{
border-bottom:1px solid #333 !important;
}

#style-options label{
position:relative !important;
}

#switch-browser-note select{
left:auto !important;
position:relative !important;
}

#left-sidebar input[type="submit"], #left-sidebar button{
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 0px 0px, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px inset !important;
background: #222!important;
color:rgb(204, 204, 204) !important;
border-style:none !important;
border:0px solid #000 !important;
-moz-appearance:none !important;
-webkit-appearance:none !important; 
-appearance:none !important;
}



textarea, input[type="checkbox"]{
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
border-style:none !important;
border:0px solid #000 !important;
-moz-appearance:none !important;
-webkit-appearance:none !important; 
-appearance:none !important;
}

#style_additional_info, textarea#css, #style_long_description{
padding-left:5px !important;
}

#additional-info, #long-description{
padding:10px !important;
background: url('http://i182.photobucket.com/albums/x146/Nihagi/lsbg.png') !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px inset !important;
padding-left:40px !important;
margin-top:10px !important;
}

#additional-info{
min-height:100px !important;
}

#additional-info-text{
background-color:rgba(0, 0, 0, 0.2) !important;
padding:10px !important;
margin-left:-10px !important;
}


#long-description{
margin-top:5px !important;
}

}
