css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xda-developers.com") {

/*GLOBAL*/
html,body,p{background:#000!important;color:#fff!important;}
footer,#footer{background:#002!important;border-color:#229!important;}

input,textarea,select
{
-moz-appearance: none !important;
color:#fd9!important;
border-color:#333!important;
background: #000 url("data:image/png;base64,R0lGODlhAQAUAIAAAAAAAP///ywAAAAAAQAUAAACBISPmQUAOw==") repeat-x !important;
}

a{color:#fd9!important;}

/*MENU*/
.menu,.menu a{background:#004!important;border-color:#229!important;}
.menu a:hover{background:#229!important;}

/*TOP STORIES-POSTS*/
.xda-window{border-color:#229!important;}

/*BODY*/
h2,h1,h4,.latest{color:#fd9!important;text-shadow:-1px 0 darkblue, 0 1px darkblue,1px 0 darkblue, 0 -1px darkblue!important;border-color:#229!important;}
h3{background:#004!important;color:#fd9!important;text-shadow:-1px 0 darkblue, 0 1px darkblue,1px 0 darkblue, 0 -1px darkblue!important;border-color:#229!important;}
.post,.p-head,.p-con p,.widget{color:#fff!important;background:#000!important;color:#fff!important;border-color:#229!important;}
.widget_head,#social,#social div{color:#fff!important;background:#004!important;text-shadow:-1px 0 darkblue, 0 1px darkblue,1px 0 darkblue, 0 -1px darkblue!important;border-color:#229!important;}

.p-con li,.p-det ul li,.p-det ul{color:#fff!important;border-color:#229!important;}
.container li{background:#000!important;border-color:#229!important;}

/*quotes*/
#_mcePaste{color:#aaf!important;}

/*drop-down feature select*/
#featureSelect{background-color:#004!important;}

/*WIKI*/
.noarticletext,#content,#content p{background:#002!important;border-color:#229!important;}
.portlet ul{background:#002!important;}
.toc,.pBody,h5{background:#000!important;border-color:#229!important;}
.pBody ul *{background:#002!important;border-color:#229!important;}
.gallerybox{background-color:#002!important;border-color:#229!important;}
.gallerybox td{background-color:#000!important;border-color:#000!important;}
dl{color:#fff!important;}

.h4,.wikitable,.wikitable td{background:#000!important;color:#fff!important;border-color:#229!important;}
.wikitable th{background:#002!important;color:#fff!important;border-color:#229!important;}

/*FORUM*/
#xda_navbar,#xda_navbar a,#forumhome_filters,#forumhome_filters li{background:#002!important;border-color:#229!important;text-shadow:-1px 0 darkblue, 0 1px darkblue,1px 0 darkblue, 0 -1px darkblue!important;}
.clearfix{background:#000!important;border-color:#229!important;}
.tcat{background:#002!important;color:#fd9!important;border-color:#229!important;}

.alt1Active,.alt1Active li{border-color:#229!important;}
#page_rightPane{background:#000!important;border-color:#229!important;}
#page_rightPane .articleBox{background:#002!important;border-color:#229!important;}
.alt1{background:#000!important;border-color:#229!important;}
.alt2{background:#002!important;border-color:#229!important;}
.forumstats{background:#002!important;border-color:#229!important;}
.forumStatsColumn,.forumLastPostColumn,.forumCollapseColumn{background:#004!important;border-color:#229!important;}

#xda_navbar td{border-color:#229!important;}

#breadcrumb span{color:#fff!important;}
.thead{background:#004!important;color:#fff!important;border-color:#229!important;}

.posterInfo,.announcementContainer,.postTitle,.postContentHead,.posterInfoHead,.posterInfoFoot,.postContentFoot{background:#002!important;border-color:#229!important;color:#fd9!important;}
.threadsTable td{border-color:#229!important;}

.submitAsTip,.moreInfoButton{background:#005!important;border-color:#229!important;}

hr{background:#229!important;border-color:#229!important;}

.replyButton{background:#229!important;border-color:#229!important;}
}
