css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('imo.im') {
#body, #footer {background: #000 !important;}
.buddygroup {background: #FFF !important;}
.button-selected {color: #FFF !important;}
#main {background: #000 !important;}
.convlog {background: #000 !important;}
.convlog * {color: #FFF !important; font-size:13px;background-color: #000 !important;}
.convlog .convlogitem.bk_old {background-color: black !important;}
.convlog-msgs {color: #FFF; background: #000 !important;}
.convlogitem {background: #000 !important;}
.ts-container .ts {font-size:13px;}
.convlog .ts-container, .convlog .ts {font-size: 13px;}
#buddypanel {background: #000 !important;color: #FFF !important; font-size:13px;}
#buddysearch {background: #000 !important;}
#buddysearch_input {color: #FFF !important;background: #000 !important;}
div.blist-container {font-size:16px !important;background: #000 !important;color: #FFF !important;}
.buddyitem {border-bottom:1px solid !important;background-color: #000;}
div.bar.white {border-top:1px solid !important;color: #FFF !important;background-color: #000;}
div.bar.white.collapsed {border-top:1px solid !important;color: #FFF !important;background-color: #000 !important;}
div.bar.white:hover {color: #000 !important;background-color: #FFF !important;}
div.buddyitem:hover {color: #000 !important;}
div.buddyitem.active {color: #000 !important;}
div.buddyitem.selected {color: #000 !important;}
.ms {font-size:13px;}
.me  .na {font-size:13px;color:#94d8f5 !important;}
.bk0 .na {font-size:13px;color:#0B97D3 !important;}
.sys_offline, .sys_online {background:rgba(0,0,0,0.25) !important;}
.sys_offline .sys * {color:red !important;}
.sys_online .sys * {color:#00EB00 !important;}
.ms a {text-decoration:underline !important;}
.ms a:visited {color: #999 !important;}
.ms a:hover {color:#94d8f5 !important;}
/*new site*/
contact.ng-scope {color: #FFF !important; font-size:7px;background-color: #000 !important;box-shadow:none;}
contact .wrapper:before {box-shadow:none!important;}
windows header {color: #FFF !important;background-color: #000 !important;}
window.ng-scope {background-color: #333 !important;}
message span.ng-scope {color: #FFF !important;}
}
