css:
@-moz-document domain("www.open.cd"), domain("open.cd") {
@-webkit-keyframes 'breathingLight' {
        from {
                background-color: #1E6B0A;
                -webkit-box-shadow:0 0 2px green;
        }
	50% {
                background-color: #2E9B12;
                -webkit-box-shadow: 0 0 65px 2px #4CB432;
        }
	to {
                background-color: #1E6B0A;
                -webkit-box-shadow:0 0 2px green;
        }
}

@-webkit-keyframes 'newmessage' {
        from {
                
                -webkit-box-shadow:0 0 30px yellow;
        }
	50% {
                
                -webkit-box-shadow: 0 0 50px red;
        }
        to {
                
                -webkit-box-shadow:0 0 30px yellow;
        }
}

div#nav
{
    width:100%;
    height:30px;
    position:fixed;
    top:0px;
    left:0px;
    background:green;
    box-shadow: 0 0 35px 1px #4CB432
}
#mainmenu
{-webkit-animation-name: breathingLight;
 -webkit-animation-duration: 7s;
 -webkit-animation-iteration-count: infinite;}

ul.menu li a,ul.menu li span 
{
    border:none;
    height:30px;
    font-size:15px;
    font-family:microsoft yahei;
    line-height:30px;
    padding:0 7px;
    background:transparent;
    font-weight:bolder;
}

div#torrentbycheck
{
position:fixed !important;
top:38px !important;
right: 0px !important; 
left:1210px !important;
}

body
{
background:url(http://i.imgur.com/hsGpp.jpg)
}

table.head
{
  background-position:center;
}
.logo_img
{
margin:0px auto;
}

td.clear.nowrap
{
display:none;
}

td
{
border:none;
}

.rowfollow
{
  border-color:yellow;
  border-style:groove;
}

table
{
background:url(http://i.imgur.com/hsGpp.jpg);
}



#info_block{
box-shadow:0 0 25px green;
border-radius: 8px;
}

h1
{
color:green;
font-size:15px;
}


p{
font-size: 17px;
font-weight: 900;
font-family: kaiti;
color:rgb(9, 131, 33);
}

.medium .color_bonus,.color_invite,.color_slots,.color_ratio,.color_active,.color_connectable
{
font-size: 10pt;
font-weight:500;
color:brown;
}

p a:visited,a:link
{
color:#147908;
font-size:14px;
text-shadow:0 0 0.5px green;
}

table.main
{
background:url(http://i.imgur.com/hsGpp.jpg); 
box-shadow:0px 2px 15px green;
border-radius: 10px;
}

td.colhead
{
box-shadow:inset 0 0 20px yellow;
font-size: 17px;
font-family: kaiti;
font-weight:900;
color:yellow;
background:green;
border-radius: 2px;
}

img.unlocked
{
width: 35px;
height: 36px;
background:url(http://i.imgur.com/dov0z.png) 85px 98px;
}

img.unlockednew
{
width: 35px;
height: 36px;
background:url(http://i.imgur.com/dov0z.png) 232px 189px;
box-shadow: 0 0 20px yellow
}

.unlockednew
{
-webkit-animation-name: newmessage;
-webkit-animation-duration: 7s;
-webkit-animation-iteration-count: infinite;
}










}

@-moz-document domain("www.open.cd"), domain("open.cd") {
@-webkit-keyframes 'toptorrent' {
        from {
                background:rgba(247, 237, 9,0.5);
        }
	50% {
                background:rgba(247, 237, 9,0.05);
        }
	to {
                background:rgba(247, 237, 9,0.5);
        }
}
.thirtypercentdown_bg
{
-webkit-animation-name: toptorrent;
-webkit-animation-duration: 7s;
-webkit-animation-iteration-count: infinite;
}


}
