css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slickdeals.net") {

/* Front Page */
#global_feature_content {background-color: #16171A !important;}
#fp3_deals_header, #slickdealswelcome  {background-color: #25292E !important;}
.fpsubmenu > :not(.active) {background-color: #193051 !important;}
.gw_title a {color: #8DC3FB !important}
.gw_bottombar  {background:url("http://i.imgur.com/41ZvGA2.png") !important;}
#featured_stores_fp, #featured_deals_header, #deal_list_featured, #cto_banner_content {display: none;}
.colitem a {color: #9EC3FB !important}

#classic_search_box, #newsearch_box, #userbar_search_box_container, #see_all_ls, #see_all_ls label, a.ls_advanced, .grid .dealblockimg {background: #222!important; color: #ddd!important; border-color: #888!important;}
.button {background: #888!important; border-color: #888!important;}
select {background: #111!important; color: #ddd!important; border-color: #333!important;}


/* Front Page - Black Friday sidebox*/
.bf_colitem a {color: #9EC3FB !important}
.bf_colitem > .votebar_small {color: #BBB !important;}
.bf_colitem {background-color: #1B2737 !important; border: 1px solid #333 !important;}
.bf_colitem:hover {background-color: #000 !important;}
#blackfriday_threadbox {border: 1px solid #333 !important; box-shadow: none !important;}
#blackfriday_threadbox label a {background-color: #383838 !important; color: #FFF !important;}
 
/* Forum avatar and stats */
.sd_postbit_usersection.ui-corner-right-small.ui-corner-top-small {background-color: #2C323E !important;}
.sd_postbit_usersection.ui-corner-right-small.ui-corner-top-small ul {background-color: #2C323E !important; color: #AFAFA3 !important;}

/* Forum listing and pages*/
.status-old, .subforum-main-info td {background-color: #1A1D20 !important;}
.td-sfstatsleft {color: #DDD !important;}
.subforum-title {color: #9EC3FB !important;}
.forumwrap {background-color: #1A1D20 !important;}
.navbar h2 {color: #EEE !important;}
.more_category {color: #3F79CC !important;}


/* Related deals sidebar */
.relatedwidget_category, .relatedwidget_store {background: #23292F !important; border-top-color: #9EC3FB !important;}
.relatedwidget_category h3, .relatedwidget_store h3 {background: #2B4F83 !important; }
.relatedwidget_category a, .relatedwidget_store a {color: #E4E4E4 !important;}
.subBoxContent a {color: #EEE  !important;}
.subBoxContent .highlight {background: #20385B !important}


/* Related deals carousel */
#related-deals-carousel {background: #1A1D20 !important;}
.jcarousel-list li {background: #23292F !important;}
.relatedbit-imagebox {border: 1px solid #23292F !important;}
.votebar {background: #2B4F83 !important;}
.votebar .comments {color: #FFF;}

 
/* Search results */
#afscontainercontainer {display: none;}
#dealalerts_subscribe_widget, #dealalerts_subscribe_widget_subscribed {background: #23292F !important;}
#results_body {background: #111 !important;}

#results_body td:nth-child(-n+2) {background: #23292F !important;}
#results_body td:nth-child(n+3) {background: #191D20 !important;} 

#results_body tr:hover td:nth-child(-n+2) {background-color: #172E42 !important;}
#results_body tr:hover td:nth-child(-n+2).title.gradient {background-color: #172E42 !important;}
#results_body tr:hover td:nth-child(n+3) {background: #111 !important;} 

#results_body #results_table td.post.header {background: #16171A !important;}

/* Store Results Page */
#see_also_stores, .storepage_info.coupons {background: #111 !important;}
.storepage_info.coupons h1, .coupon_item_main strong {color: #E2E2E2 !important;}
  
/* Compose private message */
.newpm_box {background-color: #16171A !important;}

/* Deal Alerts 2.0 */
.dealalerts_listing {background-color: #2C323E !important; color: #E2E2E2 !important;}
#contents {background-color: #1A1D20 !important;}
.btn_grey {background: -moz-linear-gradient(center bottom , #4F769E 20%, #7FBFFF 80%) repeat scroll 0% 0% #DDD !important;}
#dealalerts_release_msg {background-color: #4DC0F2 !important; color: #000 !important;}
#dealalerts_release_msg a {color: #EEE !important;}
#sddealalert_header h1 {color: #DBDBCE !important;}
.addtracker_link {color: #FFF !important;}

#adddealalert_contain, .formsection {background-color: #2C323E !important;}
.addnew_right {background-color: #1A1D20 !important;}
.filterpopup .header {background-color: #2C323E !important;}
.minimum_thread_rating_form {background-color: #393E4E !important;}
#search-form.search-dealalert {color: #2E6EBF !important;}


/* Mobile Forum Listings */
.alt1 {background-color: #23292F !important;}
.alt2 {background-color: #1A1D20!important;}
.alt1, .alt2 {color: #FFF !important;}
.alt1 a, .alt2 a {color: #9EC3FB !important;}

}
