css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.furaffinity.net") {
/* --- Bring back the BANNER and ADS? Delete everything between below up to the next comment ---*/
    #fa_header, .ads, #news { display:none !important;}
    .block-banners{
        margin-bottom: 0 !important;
        height: 10px !important;
    }
/* --- STOP ABOVE THIS LINE! ---*/
/* --- Bring back the SHADOW THUMBNAIL? Delete everything between below up to the next comment ---*/
center.flow img, center.flow s>a, #description_popup{
    border-radius: 0 !important;
    }

center.flow s>a,
center.flow s>a:hover{
    border: 2px solid #333 !important;
}
center.flow b.r-mature s>a,
center.flow b.r-mature s>a:hover{
    border: 2px solid #697cc1 !important;
}
center.flow b.r-adult s>a,
center.flow b.r-adult s>a:hover{
    border: 2px solid #971c1c !important;
}

#description_popup{	border: 1px solid #000 !important; }

center.flow s>a, center.flow s>a:hover, center.flow b.r-mature s>a, center.flow b.r-mature s>a:hover,
center.flow b.r-adult s>a, center.flow b.r-adult s>a:hover, #description_popup {
    -moz-box-shadow: 0 0 0 #000 !important;
    -webkit-box-shadow: 0 0 0 #000 !important;
    box-shadow: 0 0 0 #000 !important;
}
/* --- STOP ABOVE THIS LINE! ---*/

    body>div, .maintable, .alt1, .cat, .lead, .alt2 {
        background-color: #ddd !important;
        color: #000 !important;
    }
    
    body, body > div {
        background: #eee !important;
    }
    
    .header_bkg {
        background-color: #333 !important;
        color: #f00 !important;
    }
    .header_bkg :visited, .header_bkg :link {
        color: #eee !important;
    }

    .container-comment TD.cat A { background: #dcc !important;}
    
    input[type="text"], select, textarea, input[type="password"], button {
        background: #fff !important;
        color: #000 !important;
        border-color: #bbb !important;
    }
    
    table, td, td.alt1, td.cat, .maintable, table.alt1, table.cat, th {
        border-color: #ddd !important;
    }
    
    .links, .lead, .cat, .fieldset, .hovertable_head, .thumbnail,
    .ucp-site-settings table td, .ucp-site-settings table th, .message-center-comments h3,
    div#messagecenter-other ul.message-stream li.section-controls,
    div#messagecenter-other #messages-watches ul.message-stream li.section-controls {
        border: none !important;
    }
    .container-comment { border: 1px solid #ccc !important;}
    
    div#stats-submission table.submissions td.preview,
    div#stats-submission table.submissions td.info,
    div#stats-submission table.submissions td.desc {
        border-color: #ccc !important;
    }

    input[type="button"].button, input[type="submit"], a.newest, a.oldest, a.more, .button {
        background: #fff !important;
        color: #b00 !important;
        border-color: #900 !important; 
    }

    .prev, .next { padding: 5px !important; }
    
    input.remove-checked {
        background: #fee !important;
        color: #b00 !important;
    }
    
    input.remove-nuke {
        background: #800 !important;
        color: #fdd !important;
    }

    .messagecenter b > u {
        background: none !important;
    }

    .message-stream li:hover, .messagecenter b:hover {
        background: #ddd !important;
    }
    
    .message-stream li.section-controls:hover {
        background: none !important;
    }
    
    ul.dropdown, ul.dropdown li, ul.dropdown ul, ul.dropdown li a {
        text-transform: lowercase !important;
        color: #eee !important;
        background: #333 !important;
    }
    
    ul.dropdown li a:hover, ul.dropdown li a:active {
        color: #fff !important;
        background: #900 !important;
    }
    
    .alt1, u {
        background: #eee !important;
    }

    body > div > .maintable,
    body > div > .maintable > tbody > tr > td,
    body > div > .maintable > tbody > tr > td > table,
    body > div > .maintable > tbody > tr.innertable,
    body > div > .maintable > tbody > tr.innertable > td,
    .logo .alt1 {
        background: none !important;
    }
    
    #messagecenter-other {
        color: #333 !important;
    }
    
    A:link {
        color: #b00 !important;
      
    }
    A:visited {
        color: #900 !important;
    }
    
    .tab u, .tab a {
        border: 1px #bbb solid !important;
        border-bottom: none !important;
        margin: 0 !important;
        padding: 3px 10px !important;
        text-transform: lowercase;
    }
    .tab a { background: #ddd !important;}
    .tab u { background: #fff !important;}
    .tab [color] { color: #f55 !important;}
    
    .message-stream table.checked, .message-stream li:hover{
    border-radius: 5px !important;
    }

    .checked {
        background: #ccc !important;
    }
    
    .container-comment TH.lead H4{
	   color: #333 !important;
    }
    div#messagecenter-submissions h3.date-divider{
    	border-bottom: 1px solid #bbb !important;
    }

    #description_popup{
	   border: 1px solid #000 !important;
	   background-color: #eee !important;
    }
    #description_popup h5{
	   background-color: #fcc !important;
    	color:  #000 !important;
    }

    center.flow b.r-mature i.icon{
       background-image:url('http://fc00.deviantart.net/fs70/f/2012/238/0/7/faicons_mature_by_cheezyweapon-d5cgtvq.gif') !important;
    }
    center.flow b.r-adult i.icon{
       background-image:url('http://fc07.deviantart.net/fs71/f/2012/238/d/e/faicons_adult_by_cheezyweapon-d5cgtvv.gif') !important;
    }
    center.flow b.r-general i.icon{
       background-image:url('http://fc06.deviantart.net/fs71/f/2012/238/b/3/faicons_general_by_cheezyweapon-d5cgtvt.gif') !important;
    }

    .popup_date, .message-stream li em{
    	border-bottom: 1px dotted #bbb !important;
    }

    .sfw-toggle a:link {
        color: #fcc !important;
        text-transform: uppercase !important;
    }

    ul.dropdown li.noblock a { padding: 7px 0 !important; }
    ul.dropdown li.noblock a:first-child {padding: 7px 7px 7px 10px !important; }
    ul.dropdown li.noblock a:last-child { padding: 7px 20px !important; } 
    ul.dropdown li.noblock { padding: 0 !important; }

.menu li ul li{
	border: 1px dotted #ccc !important;
	background-color: #fff !important;
	padding: 3px;
	width: 150px !important;
}
.menu li ul li:hover{
	border: 1px dotted #f00 !important;
	background-color: #fdd !important;
	color: #000 !important;
}
/* --- New since 9 12 ---*/
#admin_notice_do_not_adblock{
	border: 1px solid #ddd !important;
	}
#admin_notice_do_not_adblock TD{
	background-color: #fff !important;
}
/* --- Gets rid of pointless already broken warning image ---*/
#admin_notice_do_not_adblock > tbody > tr > td > table > tbody > tr > TD:first-child {
    display: none !important;
}
#admin_notice_do_not_adblock H4{
	color: #d00 !important;
}
}
