css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com") {

}

#dashboard_tumblelog_settings #main_column, #dashboard_tumblelog_settings #page_nav{
background-color:#fefefe !important;
}

#dashboard_tumblelog_settings .clearfix{
background-color:#fefefe !important;
border-radius:15px !important;
-moz-border-radius:15px !important;
-webkit-border-radius:15px !important;
-o-border-radius:15px !important;
-ms-border-radius:15px !important;
}

.popover_menu > .popover_inner, .popover_menu > .popover_inner a, .popover_menu > .popover_inner .hide_overflow{
color:#000000 !important;
}

#right_column:after{
background:none !important;
}

#dashboard_index, #dashboard_inbox, #main_help, #dashboard_settings, #dashboard_drafts, #dashboard_followers, #dashboard_submissions, #dashboard_post_queue, #tagged_display, #dashboard_following, #dashboard_likes, #dashboard_index #container, #main_help #container, #dashboard_inbox #container, #dashboard_settings #container, #dashboard_drafts #container, #dashboard_followers #container, #dashboard_submissions #container, #dashboard_post_queue #container, #tagged_display #container, #dashboard_following #container, #dashboard_likes #container, #dashboard_index #container #header_container, #dashboard_inbox #container #header_container, #main_help #container #header_container, #dashboard_settings #container #header_container, #dashboard_drafts #container #header_container, #dashboard_followers #container #header_container, #dashboard_submissions #container #header_container, #dashboard_post_queue #container #header_container, #tagged_display #container #header_container, #dashboard_following #container #header_container, #dashboard_likes #container #header_container, #dashboard_directory, #dashboard_directory #container, #dashboard_directory #container #header_container, #dashboard_lookup_contacts, #dashboard_lookup_contacts #container, #dashboard_lookup_contacts #container #header_container, #dashboard_tumblelog_settings, #dashboard_tumblelog_settings #container, #dashboard_tumblelog_settings #container #header_container, #dashboard_edit_post, #dashboard_edit_post #container, #dashboard_edit_post #container #header_container, #dashboard_upload_static_file{
background: #000000 url('http://i.imgur.com/v6ClR.gif') no-repeat fixed left bottom !important;
}

#dashboard_index #container, #dashboard_inbox #container, #dashboard_drafts #container, #dashboard_followers #container, #dashboard_submissions #container, #dashboard_index #container div.l-content, #dashboard_inbox #container div.l-content, #dashboard_drafts #container div.l-content, #dashboard_followers #container div.l-content, #dashboard_submissions #container div.l-content, #dashboard_post_queue #container div.l-content, #tagged_display #container, #tagged_display #container div.l-content, #dashboard_following #container, #dashboard_following #container div.l-content, #dashboard_likes #container, #dashboard_likes #container div.l-content, #dashboard_tumblelog_settings #container, #dashboard_tumblelog_settings #container #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color:rgba(0,0,0,0.7) !important;
}

#posts .post .permalink{
background:none !important;
}

.selection_nipple{
border-bottom:12px solid #efefef !important;
}

.nipple{
border-right:7px solid #000000 !important;
}

ul.controls_section, input#search_query{
color:#efefef !important;
background-color:#000000 !important;
}

#next_page_link, #previous_page_link{
color:#efefef !important;
background-color:#000000 !important;
border:1px solid rgba(0,0,0,0.18) !important;
}

li.notification, #posts .notification{
color:#efefef !important;
background-color:#000000 !important;
border:none !important;
margin-bottom:2px !important;
border-radius:5px !important;
-moz-border-radius:5px !important;
-webkit-border-radius:5px !important;
-o-border-radius:5px !important;
-ms-border-radius:5px !important;
}

li.notification a{
color:#efefef !important;
underline:none !important;
border:none !important;
}

li.notification a:hover{
color:rgba(239, 239, 239, 0.9) !important;
}

#posts .notification .notification_type_icon.like_icon{
background:url('http://static.tumblr.com/cxtftev/VjSm9ed33/dashboard-like-icon.gif') no-repeat scroll 0px 0px transparent !important;
}

#posts .notification .notification_type_icon.reblog_icon{
background:url('http://static.tumblr.com/cxtftev/9n2m9fpnu/dashboard-reblog-icon.gif') no-repeat scroll 0px 0px transparent !important;
height:15px;
}

#posts .notification .notification_type_icon.follow_icon{
background:url('http://static.tumblr.com/cxtftev/KMHm9fpmt/dashboard-follow-icon.gif') no-repeat scroll 0px 0px transparent !important;
}

a.avatar_frame, img.avatar{
border-radius:5px !important;
-moz-border-radius:5px !important;
-webkit-border-radius:5px !important;
-o-border-radius:5px !important;
-ms-border-radius:5px !important;
}

#dashboard_inbox #container div.l-content #right_column .send_fan_mail{
color:#efefef !important;
background-color:#000000 !important;
background-image:none !important;
box-shadow:0px 1px 0px 0px #000000 inset, 0px 1px 6px 0px #000000 !important;
border:1px solid rgba(0,0,0,0.18) !important;
}

#dashboard_index #container div.l-content #right_column, #dashboard_inbox #container div.l-content #right_column{
background-image:none !important;
box-shadow:none !important;
border:none !important;	
}

#dashboard_index #container div.l-content #left_column #dashboard_controls a, #dashboard_index #container div.l-content #left_column #dashboard_controls span, #search_controls_button{
color:#efefef !important;
}

.hide_overflow, ul.controls_section a, #dashboard_index #container div.l-content #left_column #dashboard_controls .dashboard_controls_arrow_right{
color:#efefef !important;
}

#dashboard_post_queue #container div.l-content #left_column ol#posts .no_posts_found h1.dashboard_header{
color:#efefef !important;
}

#dashboard_followers .white_pane .hide_overflow{
color:#000000 !important;
}
