css:
@-moz-document url-prefix("http://www.pixiv.net/member_illust.php?mode=") {
/*pixivイラストページ*/
/*header*/
header#global-header{
border-bottom:5px solid #D6DEE5;
position:relative;top:-31px;
}
header#global-header:hover{
border-bottom:1px solid #D6DEE5;
position:relative;top:0px;}

/*位置調整*/
div.contents-east{position:relative;top:-70px;
}

/*bookmarkボタンの行*/
section.action{position:absolute !important;top:0px !important;width:755px;
z-index:700}
/*bookmarkボタン以外を非表示に*//*
ul.share-button{display:none !important;}*/
/*ブックマークボタンの横幅調整*/
div.bookmark a{width:75px !important;height:20px !important;overflow:hidden !important;}
.ui-button{padding:3px !important;}

div.works_display/*画像の表示位置*/
{position:absolute;top:40px !important;
width:99% !important;}
/*閲覧評価消し*/
section.score dt,section.score dd{display:none !important;}

div.two_column_body{border:none !important;background-color: transparent !important;}
div.two_columnBottom{display: none !important;}

div.two_column_top,
div.extaraNaviAlso,
section.work-info,
div.works_info{position:relative;left:750px;background:#FFF;}

div#tag_area,div#tag_edit{position:relative;left:730px;background:#F2F4F6 !important;
width:width: 782px !important;}

div.works_info{position:relative;left:375px !important;}

/*線*/
div.two_column_top,
div.extaraNaviAlso,section.work-info,
div#tag_area,
div.works_info{border-left:30px solid #CCC;position:relative;top:40px;
height:30px !important;width:0px !important;overflow:hidden !important;}

/*hover*/
div.two_column_top:hover,
div.extaraNaviAlso:hover,section.work-info:hover,
div#tag_area:hover,
div.works_info:hover{position:relative;left:0px !important;border:none !important;
height:100% !important;width:100% !important}
div#tag_edit{position:relative;top:40px;left:0px}




div.two_column_top,
div.extaraNaviAlso,
section.work-info,
div#tag_area,div#tag_edit,
div.works_info{z-index:800}

.ui-layout-west{z-index:801;position:relative;top:10px;left:40px !important;}
header#global-header{z-index:999}




}

@-moz-document url-prefix("http://www.pixiv.net/member_illust.php?") {
/*左右逆に*/
.ui-layout-east{position:absolute;left:10px !important;}
.ui-layout-west{position:absolute;left:800px !important;}

/*フッター非表示*/
#global-footer{display:none !important;}

}
