css:
@-moz-document url("http://www.wikipedia.org/") {

/* Cleanup
********************************************/
.langlist, .bookshelf-container, .wm-site-info, HR {display:none!important;}
body#www-wikipedia-org .search-container label {display:none!important}
#searchLanguage {display:none!important;}
.search-container.language-search {display:none!important;}
.divider {display:none!important;}


/* Central Images
********************************************/
body#www-wikipedia-org {
  background: white url('http://i.imgur.com/7jR9o.jpg') no-repeat scroll top center;
  background-position: center -70px;
}

a:link {
  color: black!important;
}

.central-featured {}

/* Input
********************************************/
.search-container {
  box-shadow: 2px 5px 20px #444;
  -webkit-box-shadow: 2px 5px 20px #444;
  padding: 0px!important;
  margin-bottom: 45px!important;
  background-color: rgba(255,255,255,0.1)!important;
}

fieldset {
  background-color: transparent!important;
  padding: 0px!important;
  margin: 0px!important;
}

input#searchInput {
  padding: 5px!important;
  margin: 50px 0px!important;
  width: 200px!important;
  color: #333!important;
  font-family: Calibri;
  font-size: 16px!important;
}

input.formBtn {
  padding: 5px!important;
  margin: 50px 0px!important;
}

/* Footer
********************************************/
.otherprojects {
  background-color: rgba(255,255,255,0.6)!important;
  padding: 10px 0px;
  border-top: 1px dashed grey!important;
}

}
