@-moz-document url("http://www.nicovideo.jp/my"),
url-prefix("http://www.nicovideo.jp/my/"),
url-prefix("http://www.nicovideo.jp/user/"){

/*フォント色変更*/
a{
  color: #666 !important;
  }
h4 a{
  font-weight: bold !important;
  color: #59698E !important;
  }
h4 a:visited{
  color: #35414F !important;
  }

/*左メニュー*/
#myNavWrap{
  background: none !important;
  padding-bottom: 0 !important;
  width: 230px !important;
  }
.myNav a,
.myNav span{
  padding-top: 6px !important;
  padding-bottom: 5px !important;
  font-size: 12px !important;
  background-position: 0 5px !important;
  }
.myNav a{
  width: 203px !important;
  font-weight: bold !important;
  text-decoration: none !important;
  font-size: 12px !important;
  font-weight: normal !important;
  color: #666 !important;
  }
.myNav span{
  color: #bbb !important;
  }
.myNav > .profile > dt.active > a,
.myNav > .profile > dt.active > span,
.myNav > dl > dd.active > a,
#myNavOthers > ul > li.active > a{
  color: #fff !important;
  font-weight: bold !important;
  }
.myNav > dl > dt,
.myNav > dl > dd,
#myNavOthers > ul > li{
  padding-top: 0px !important;
  padding-bottom: 0px !important;
  }
.myNav > .profile > dt,
#myNavOthers > ul > li{
  width: 216px !important;
  }
.myNav > dl > dd{
  width: 203px !important;
  }
.myNav > dl > dt:hover,
.myNav > dl > dd:hover,
#myNavOthers > ul > li:hover{
  background-color: #e8e8e8 !important;
  -moz-border-radius: 3px;
  -webkit-border-radius: 3px;
  }
.myNav > .profile > dt.active,
.myNav > dl > dd.active,
#myNavOthers > ul > li.active{
  background-color: #ABB9D1 !important;
  -moz-border-radius: 3px;
  -webkit-border-radius: 3px;
  }
#myNavNicorepo,
#myNavMylist,
#myNavWatchlist,
.myNav li{
  border-width: 0 !important;
  }
#myNavNicorepo{
  background: none !important;
  padding-top: 0 !important;
  }

/*カレンダー*/
.userCalendar{
  border-color: #e5e5e5 !important;
  }
.calendarInner th{
  border-width: 0 !important;
  background-color: #ccc !important;
  }
.today{
  border-color: #F7CB82 !important;
  background-color: #F7CB82 !important;
  }
.today > a{
  color: #E7850E !important;
  }
.hasData{
  border-color: #e8e8e8 !important;
  background-color: #e8e8e8 !important;
  }
.hasData > a{
  color: #6A7E97 !important;
  }
.exTag input{
  border-color: #d5d5d5 !important;
  }

/*生放送中の番組*/
.liveProgramHead{
  height: 23px !important;
  }
.liveProgramHead > p,
.liveProgramListWrap{
  background: #e3e3e3 !important;
  }
.liveProgramHead > p,
.liveProgramListWrap,
.liveProgramListInner{
  -moz-border-radius: 3px;
  -webkit-border-radius: 3px;
  }

/*メインカラム*/
.cmnHeading{
  height: 20px !important;
  }
#mypageContents{
  width: 735px !important;
  }
#myCont{
  border: 1px solid #dadada !important;
  min-height: 0 !important;
  }
#myContHead,
#myContBody{
  border: 5px solid #F4F4F4 !important;
  }
#myContHead{
  background-color: #eee !important;
  background: url("http://uni.res.nimg.jp/img/bg/bg_common_c_head.gif") -1px repeat-x !important;
  border-bottom-width: 0 !important;
  }
#myContBody{
  padding: 0 0 10px 0 !important;
  border-top-width: 0 !important;
  }
.editFormWrap,
.helpBox,
.noStamp,
.stampList,
.stampTitle,
.stampGraph,
#siteFilterBox,
#flashcontent{
  margin: 10px 20px !important;
  }
.myContList > li:nth-child(even),
#SYS_THREADS > li:nth-child(even){
  background-color: #f4f4f4 !important;
  }
.spBox,
.myContList > li,
#SYS_THREADS > li{
  border-bottom: 1px dotted #ccc !important;
  padding: 12px !important;
  }
.userSubNav{
  border: none !important;
  }

/*マイページ*/
.myContList.history .mypageThumb,
.SYS_box_item > .mypageThumb,
.editCheckbox{
  margin-left: 20px !important;
  margin-top: 5px !important;
  }
.report{
  line-height: 1.5 !important;
  }
.report > h4 > .delBtn{
  background-color: #B7C0D5 !important;
  font-weight: normal !important;
  color: #fff !important;
  padding: 1px 10px !important;
  -moz-border-radius: 3px;
  -webkit-border-radius: 3px;
  }
.report > h4 > .delBtn:hover{
  background-color: #C7CEDE !important;
  }
.report > h4 > .time,
.time{
  font-weight: normal !important;
  color: #888 !important;
  }
.note,
.report > h4 > .time.imdt{
  font-size: 12px !important;
  font-weight: bold !important;
  color: #E2625F !important;
  }
.highlight{
  font-weight: bold !important;
  color: #333 !important;
  }
.highlight > a{
  background-color: #D0D6E3 !important;
  padding: 3px 10px !important;
  -moz-border-radius: 3px;
  -webkit-border-radius: 3px;
  }
.resForm,
.res{
  background: none !important;
  }
.myResText{
  border-color: #B1B9CD !important;
  }
.commentBtn > a{
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAUCAAAAACPTMM5AAAAHUlEQVQI12P8z8DAwATEDCz/UQhsYhDiHy4JXNoAVdURNoh7/d4AAAAASUVORK5CYII=") !important;
  -webkit-border-radius: 4px;
  -moz-border-radius: 4px;
  border: 1px solid #d5d5d5 !important;
  height: 20px !important;
  color: #666 !important;
  font-size: 11px !important;
  }

/*マイリスト*/
.myContList > li{
  min-height: 35px !important;
  }
.myContList > li > p{
  width: 460px !important;
  color: #555 !important;
  line-height: 1.5 !important;
  }
.myContList.mylistList h4 > a,
.mylistVideo > h4 > a{
  font-size: 14px !important;
  }
.mylistTime{
  font-size: 11px !important;
  color: #999 !important;
  }
.mylistVideo > dl{
  font-size: 11px !important;
  color: #666 !important;
  }
.mylistVideo dd > a{
  text-decoration: underline !important;
  }
.noListMsg{
  border: none !important;
  }
.noListMsg > p{
  color: #6A7E97 !important;
  }

/*サムネイルのボーダー*/
.userThumb a,
  .mypageThumb{
  border-color: #bbb !important;
  }

/*ユーザーデータ*/
.watchFriend li{
  border-top: none !important;
  }
.profile > dl{
  border-bottom: 1px dotted #ccc !important;
  padding: 12px !important;
  }
#accountBox{
  border: none !important;
  }
#accountBoxWrap{
  border: 1px solid #dadada !important;
  }

/*コミュニティ*/
.report > p{
  color: #555 !important;
  line-height: 1.5 !important;
  }
.report > dl{
  color: #999 !important;
  }

/*細部*/
#headingMypage a{
  color: #888 !important;
  }
#watchedNum{
  font-size: 18px !important;
  font-weight: bold !important;
  color: #6A7E97 !important;
  }
.exp,
.icon.iconListMarkArwGray > span,
.mypageBtn.large em{
  color: #E2625F !important;
  }
.userSubNav a,
.spMsg,
.att,
.viewing > span{
  color: #8894B7 !important;
  line-height: 1.5 !important;
  }
}
/*fixed add*/
.contents{margin-top: 19pt!important;}

