@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("live.nicovideo.jp") {

#middle_ad,#pickup_view,.carousel.toppicup_area,
#siteHeaderNicopoPurchase,
#jsFollowingAdSide,#jsFollowingAdContent
{display:none!important;}


#siteHeader #siteHeaderInner ul li{height: 24px!important;
line-height: 24px!important;}

body{padding-top: 0px!important;}

#page_cover{position:relative;
top:-28px;
margin-bottom: 0px!important;}
#utility_link{display:none!important;}

#description_tab_view,#commu_info{margin:15px!important;}


#netduetto_tab,#netduetto_tab_view{display:none!important;}
.slider_container{padding:0px!important;}


#watch_tab_box{padding:0px!important;}
#advertisement_box .box_inner{padding:0px!important;}

#passed_program_area,.feature_area,.blog_area,.search_program_area,#favorite_program_area,.for_premium,#onair_program_area{display:none!important;}

/*new topbar edit 20131115*/



#watch_title_box{padding-top: 0px!important;
padding-bottom: 0px!important;
height: 0px!important;
box-shadow: none!important;
border: none!important;
}

h1.logo{display:none!important;}
.score_search{  top: 14px!important;
  left: 0px!important;
width: 100%!important;
}
.score_search p{  margin-bottom: 0px!important;
  margin-left: 0px!important;
width: 50%!important;
}
.search_program{position:relative;top:-16px;left:150px;}

.search_word{padding:0px!important;
border:none!important;
box-shadow:none!important;
}
.search_program .submit {height: 14px!important;
border:none!important;}


.thumb_area{display:none;}
.title_text{font-size: 10px!important;}
.title{margin: 0px!important;}
.text_area{ height: 16px!important;}
#watch_title_box .meta{padding-top: 0px!important;
margin-top: -10px!important;
 font-size: 10px!important;
 height: 16px!important;
 width: 50%!important;
position:relative;
top:-17px!important;left:60%!important;
}

/*20121229 edit*/


#taglist{padding-top: 0px!important;
padding-bottom: 0px!important;
background-color: white!important;
}

#NicoliveTimeline,#siteFooter,.copyrights,

#logo,.siteHeaderGlovalNavigation,
.live_score_list span,.siteHeaderAva,#siteHeaderNotificationContainer,#siteHeaderNotificationPremium,
#watch_player_bottom_box,#body_footer_wrap,#page_footer,.bgUnder,.bgUp,#pickupFrame,
#watch_zapping_box,.favorite,#watch_niconicoukoku_box,.provider_icon,#alert_mobile,
TD[width="100%"],DIV#ichiba_placeholder>TABLE[width="100%"]>TBODY>TR>TD,#ad_bnr,#watch_like_buttons,.iconInfo,.caution,#js_pastProgram.pastProgram,
.onAirOfficialProgram h2.sprite,.ssd,#w_nv2,.sprite_wtc.tag,
#footer_ads,IMG[src*="./img/nicolive/bnr_nicolive_premium_ts/"],
#siteHeaderRightMenuUnfix,#infoArea
{display:none!important;}

.live_score_list{position:fixed;
top: 1px;left: 22px;
width: 60px!important;
z-index:9999;}

.search{position:fixed;
top:0px;
left:95px;
z-index:9999;
}
.live_score_list strong{color: #fff!important;}

#live_global_nav{position:relative;
top:-22px;}

#watch_tab_box{padding-bottom: 0px!important;}
.view{padding-top: 0px!important;
background:#f3f3f3!important;}
#commu_info div,
.view b{font-size:65%!important;}

#watch_tab_box #tab_title_area{background-image:none!important;}



#all .container .container .contents{margin-top: -70pt!important;
z-index:-9999!important;
}

#watch_tab_box .box_inner{padding-top: 0px!important;
background-color:#272727!important;}


.infoTypeAir{position:relative;
background:white!important;
top:-92px;

}
#js_pastProgram{background:white!important;}

#ichiba_placeholder{margin-top: -74pt!important;}

/*timetable sinjuku*/
#all #index #description,#all #index #icon-description,
#timetable,.tabs-bottom,#slider,#slidel,#slidet,#shadow-r,#shadow-t,#shadow-d,#shadow-l,#slideb,#slidea,#lane-footer
{display:none!important;}
#index{margin-top:-25px!important;}


/*zero header*/
#search_target{position:relative;
top:1px;left:-25px;
border:none!important;
background:white!important;
padding-top: 0px!important;
padding-bottom: 0px!important;
height:11px!important;
vertical-align: top!important;
}
#score_search{position:relative;
top:17px;}
#score_search{margin-top:0px!important;}
.live_score_list{padding:0px!important;
text-shadow: none!important;
}
#page_cover{margin-top:10px!important;}

#siteHeaderInner{height:15px!important;
background:#303030!important;
width:100%!important;}
#live_header{height:55px!important;}


#siteHeaderNotification{border:0px!important;}

#score_search > li.search form input.submit{display:none!important;}
.live_score_list{margin-left: 6px!important;
padding: 0px!important;}


#body_header .siteHeaderMenuList{
position:fixed;
right:0px!important;
font-size:10px!important;
top:-5px;}
#siteHeader #siteHeaderInner ul li a,
#siteHeader #siteHeaderInner ul li a:hover{ border:none!important;
}
#siteHeader #siteHeaderInner ul li{height: 18px!important;}
#body_header #siteHeaderRightMenuContainer{top: 18px!important;}
#body_header .siteHeaderMenuList li ul li a{ padding-top: 0px!important;
  padding-bottom: 0px!important;}

}

@-moz-document url-prefix("http://live.nicovideo.jp/recent") {
#cruise,.ctgr10,#program{display:none!important;}

#page_cover{margin-top:5px!important;
margin-bottom: 0px!important;
padding-bottom: 0px!important;
}
#mainNavWrap{margin-top:8px!important;}
}
@-moz-document url-prefix("http://live.nicovideo.jp/watch") {
/*title*/
body{background-color: #141414!important;}
#watch_title_box img:nth-child(2),
IMG[src^="http://icon.nimg.jp/community/s/"],
IMG[src^="http://nl.simg.jp/img/"]{
width: 25px!important;
height: 25px!important;
margin-top: 0px!important;}
#watch_title_box{height: 10px!important;
border:none!important;}
#watch_title_box h2,
#watch_message_box li,
#watch_title_box a,
#watch_title_box span
{padding-top: 0px!important;
line-height: 0.9!important;
font-size: 10px!important;
height: 10px!important;
}
span.arrow_bottom{display:none;}



/*tag*/
h3.tag_title{display:none!important;}
ul.tag_list{font-size: 10px!important;
padding-top: 0px!important;}
#livetags{display:block!important;}
.livetag_box{padding-bottom: 0px!important;
height: 42px!important;}
#watch_player_top_box div div.livetag_box div.cover{
padding-top: 0px!important;height: 30px!important;
margin-bottom: 0px!important;
margin-left: 0px!important;}
span.open{display:none!important;}
#livetags a,#livetags{color:#f1f1f1!important;}
#livetags .edit{position:absolute;
left:5px;}
/*タブ開閉*/
/* Table of Contents Box == Icon - http://p.yusukekamiyamane.com/ */

#watch_player_top_box {
position: absolute!important;
top: -20px !important; left: 5px !important;
height: 16px !important;
width: 16px !important;
display: block !important;
border: none !important;
overflow: hidden !important;
z-index: 9999 !important;
text-indent: -9999px !important;
padding: 0px !important;
margin: 0 !important;
opacity: .6 !important;
}

#watch_player_top_box:hover {
display: table !important;
background-image: none !important;
width: auto !important;
height: auto !important;
background-color: rgba(0,0,0,.82) !important;
outline: 2px solid rgba(180,180,180,.82) !important;
text-indent: 0 !important;
opacity: 1 !important;
}

/* ToC text styling */

#watch_player_top_box H2 {color: #EEE !important;}
#watch_player_top_box a  {color: #BACFF2 !important;}
#watch_player_top_box a:visited {color: #DDC6F2 !important;}
#watch_player_top_box a:hover {color: #FFE900 !important;}

#toc .toctoggle {display: none !important;}
.editsection,
#localNotice P{display: none !important;}

/*ichiba*/
#ichiba_itemA{
margin-top: 99px!important;
background:#f3f3f3;}

.bpn_fkd_big p,
.bpn_fkd_sml span,
.bpn_fkd_big img,
.bpn_fkd_sml img,
.bpn_fkd_sml p{display:none!important;}
.bpn_fkd_big a{background-image:none!important;
padding-top:0px!important;
padding-bottom:0px!important;
font-size:9px!important;
}
.bpn_big_pho{padding:0px!important;
width: 50%!important;
height: 50%!important;}


#bpn_display_big div.slot, #bpn_display_sml div.slot{padding:0px!important;}

/*tab*/


#mypage h2.bar,
.ctgr10,
.timeshift_num_ad_premium{display:none!important;}
#mainNavWrap.spriteNav{margin-top:36px!important;}

#liveList{margin-top:-100px!important;}
}
@-moz-document url-prefix("http://live.nicovideo.jp/search") {
/*search*/
#live_header,
#form_frm_btm td:nth-child(2),#select_mode_area,
#all_cover #all #header,
#form_frm_top{display:none!important;}

#bar_search{height:14px!important;}

.form_result_2{width: 250px!important;
padding:0px!important;
position:fixed;
z-index:9999;
left: 176px;
top: 3px;
width: auto!important;
color:white!important;
font-size: 10px!important;
}
#sort_select_area{top: 0px;
left: 575px;
position:fixed;
z-index:9999;
font-size: 6px!important;
padding-top: 0px!important;
padding-bottom: 0px!important;
border: 0px!important;}
#sortselect{font-size: 8px!important;}

#search_form table{border:none!important;}
#bar_search{padding-top:0px!important;
padding-bottom:0px!important;
height:12px!important;}
.search_input{background-image:none!important;
background:white!important;
border:none!important;
font-size: 10px!important;
font-weight:normal!important;
width: 150px!important;
position:fixed;
top: 1px;left: 17px;
z-index:9999;}



#form_frm_btm{background:none!important;
padding:0px!important;}

#tag_area{padding-top: 0px!important;
padding-bottom: 0px!important;
margin-bottom: -10px!important;
}
#option_toggle_area,#option_provider_area,
.thmb_community,.thmb_bkimg,
.community_area,#search_right,.notice_msg,
.result_list:nth-child(8),.result_list:nth-child(10),.recommend_list{display:none!important;}

.thmb_area{width: auto!important;}
.score_area{float:left!important;
margin-top:-7px!important;}

#search_left table tbody tr td:nth-child(2),
#search_left div div div table tbody tr:nth-child(3),.thmb_area{width: 26px!important;}

.result_item,#search_left span,.status{
padding-top: 0px!important;
padding-bottom: 0px!important;
color:#c0c0c0!important;}

.result_item.cmn p,
.result_item.cmn span:nth-child(3){
margin-left:-85px!important;
}
#search_left{margin-top: 20px!important;
margin-bottom: 0px!important;

}

body{padding-top: 0px!important;}

a:link{color:#c0c0c0!important;}
a:hover{background:none!important;}
a:visited{color:#f1f1f1!important;}

/*アイコン*/
.thmb_icon{padding-top:3px!important;
width:40px!important;
height:40px!important;
}
.thmb_icon_community{
border:none!important;}

}
