css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://hackaday.com"), url-prefix("https://hackaday.com") {

    body {
        color: #333 !important;
        background-image: none !important;
    }

    #wrap, #header, #tl_ad, body, .entry {
        background-color: white !important;
    }

    h3, h5, h6 {
        color: #111 !important;
    }

    h4, a {
        color: #0a0 !important;
    }

    a:hover {
        color: #000 !important;
    }

    .widget, #cat {
        background-color: #eee !important;
        border: solid black;
    }

    .sidebar {
        color: black !important;
    }

    body, p, .wrap {
        color: #333 !important;
    }

    #wp-calendar td, thead {
        background-color: #ddd !important;
    }

    .even {
        background-color: #fff !important;
    }

    .odd {
        background-color: #fafafa !important;
    }

    #footer, .menu {
        background-color: #eee !important;
    }
    
    .date {
        color: black;
    }
    
    .post {
        border-bottom: 0px !important;
    }
    
    input, select, textarea, option {
        background-color: white !important;
        color: #111 !important;
    }
    
    #cat {
       background-color: white !important;
    }
}
