css:
/* Имя: Улучшенное оформление для iGuides.
Не ломает и не режет глаза.
© Аванесян Х.С. aka XpucT,
© Vladimir Sobolev */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.iguides.ru") {

.alt1, .alt1Active 
{ 
color: #000000 !important;
/*text-align: justify !important; */ /* Выравнивание новости по ширине строки */
font: 13px "Verdana", "Gill Sans", "Gill Sans MT", "Calibri", sans-serif !important;
text-shadow: 0px 1px 0px rgba(245, 245, 245, 0.992) !important;
}

.alt2, .alt2Active {
font: 13px "Verdana", "Gill Sans", "Gill Sans MT", "Calibri", sans-serif !important;
}

.thead {
font: 12px "Verdana", "Gill Sans", "Gill Sans MT", "Calibri", sans-serif !important;
}

.blog_bg_post { 
color: #3E3E3E !important;
font: 13px "Verdana", "Gill Sans", "Gill Sans MT", "Calibri", sans-serif !important;
}

.showthread_news { 
/*text-align: justify !important; */ /* Выравнивание новости по ширине строки */
color: #000000 !important;
font: 13px "Verdana", "Gill Sans", "Gill Sans MT", "Calibri", sans-serif !important;
text-shadow: 0px 1px 0px rgba(245, 245, 245, 0.992) !important;
}

.alt2, .alt2Active { 
text-shadow: 0px 1px 0px rgba(245, 245, 245, 0.992) !important;
}

hr{
border:0px; 
border-top:1px solid #CCC
}

.blog_nick{
color:#CCC !important;
}

.blog_button { 
font: 12px "Verdana", "Gill Sans", "Gill Sans MT", "Calibri", sans-serif !important;
}

.blog_reputation a,
.blog_reputation a:hover { 
color: #16638D !important;
}

td, th, p, li { 
font: 11pt "Arial", "Gill Sans", "Gill Sans MT", "Calibri", sans-serif !important;
}

.text_rest { 
font: 8pt "Tahoma", "Gill Sans", "Gill Sans MT", "Calibri", sans-serif !important;
}

.quote_code { 
color: #505050 !important;
font: 11pt "Myriad Pro", "Gill Sans", "Gill Sans MT", "Calibri", sans-serif !important;
}

.blog_text { 
font: 12px "Verdana", "Gill Sans", "Gill Sans MT", "Calibri", sans-serif !important;
}

.tcat { 
font: 700 11pt "Verdana", "Gill Sans", "Gill Sans MT", "Calibri", sans-serif !important;
}

/* by VS */

img[src*="banner"] {
display:none!important;
}

div#scrollbar2 {
height:20px;
overflow:hidden;
margin-top:-30px;

	-webkit-transition: height .4s ease-in-out;
	-moz-transition: height .4s ease-in-out;
        -ms-transition: height .4s ease-in-out;
        -o-transition: height .4s ease-in-out;
	transition: height .4s ease-in-out;
}
div#scrollbar2 .viewport {
	opacity:0;
	padding-top:30px;

	-webkit-transition: opacity .4s ease-in-out;
	-moz-transition: opacity .4s ease-in-out;
        -ms-transition: opacity .4s ease-in-out;
        -o-transition: opacity .4s ease-in-out;
	transition: opacity .4s ease-in-out;
}
div#scrollbar2:hover {
	height:230px;
}
div#scrollbar2:hover .viewport {
	opacity:1;
}

td[width="240"] div[align="center"] {
	display:none!important;
}

}
