/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gamespot.com") {
.username a {
    color: #1B9E09 !important;
    background: #111 !important;
}
.quote, .reply, .crumbs a, .eb_wrap a, .module_title a, h3.title a, #browse_filters .wrap a, .message a, .panelItemLinkWrap a, .page_nav_wrap a, .tab_text span, .metaDataArticle a, .board_description a, .topics .topic_title, .primary a, .topics .lastpost a, .topics .unread, .pm, .foot .first a, .blog_entries a {
    color: steelblue !important;
}
.msg_head, #site_footer {
    background: #111 !important;
}
.full_text .wrap .search_text, .masthead_search fieldset .wrap {
    background: #333 !important;
}
.bb_quote {
    border-radius: 6px !important;
}
.sticky_topics .topic .topic_title, .sticky_topics .lastpost a, .sticky_topics .unread {
    color: #50A0A0 !important;
}
.votes > .total {
    background: #333 !important;
}
.total .perc {
    background: steelblue !important;
}
.votes > .perc {
    background: #1F1F1F !important;
}
.inner_wrap {
    width: 100% !important;
}
.head {
    background: #444 !important;
}
.promo_types, #cbs_footer, .site, .site_mf_wrap, .feed, .socialMediaAccounts, #side {
    display: none !important;
}
input[type="submit"], input[type="button"], select {
	background: #444 !important;
	color: #aaa !important;
	border: 1px solid gray;
	-moz-appearance: none;
	-webkit-appearance: none;
	font-size: 9pt !important;
}
input[type="checkbox"] {
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	background: rgba(0,0,0,.4) !important;
	border: 1px solid rgba(255,255,255,.3) !important;
	height: 10px !important;
	width: 10px !important;
}
	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select, input[type="file"] > input[type="text"] {
	-moz-appearance: none;
	-webkit-appearance: none;
	font-size: 9pt ! important;
	color: #fff !important;
	background: #333 !important;
	border-color: gray !important;
	border: 1px solid;
}
.action > .forum_button {
    background: #000 !important;
    border: 1px solid #777 !important;
    border-radius: 1px !important;
}
#filterform span.submit {
    background: #444 !important;
    border: 1px solid #777 !important;
    border-radius: 1px !important;
}
}
