css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ps3hax.net") {
td, p, blockquote, .member_blockrow, dd, dt, em, textarea, h3 span, .comments, h5, font, .category, .date, li, h2, h1, h3, pre, blockquote p span {
    color: #eee !important;
}
a {
    color: steelblue !important;
}
.panel, div#main, #wrapper, .spoiler, .mainbox .tabs, .postbody, .navlinks, .inner_block, .blockfoot, .blockrow, .quote_container, .textcontrols.floatcontainer, .postdetails, .bbcode_code, .userinfo, .sticky, .sticky .threadstats, .body_wrapper, .header, .post > .content, .entry, .spoiler-body, .footer, .post, .spoiler-wrap {
    background: #2C2C2C !important;
}
#menu2 {
    background: none !important;
}
td, .alt1, .alt2 {
    background: #2D2D2D !important;
}
.nonsticky, .activitybit, .forumrow, .mainbox .current, .mainbox, blockquote, .comment-paragraph, .cke_source, .nonsticky .threadstats, .has_after_content, .title.icon {
    background: #333 !important;
}
.blockhead, .thread-even, .forumhead, .thread-odd {
    background: #111 !important;
}
center, .header, .box, #myvideo, #sidebar_container {
    display: none !important;
}
.post {
    width: 144.5% !important;
    border: 2px solid #666 !important;
    border-radius: 8px !important;
}
#gallerycontent, #content {
    width: 100% !important;
}
/*#main #content .post .content p img {
    text-align: center !important;
}*/
.content, .galleryleft {
    background-color: transparent !important;
}
.attention-box {
    background: #000 !important;
}
em,h3,a,h4,h2,h1 {text-shadow:none!important;}
}
