css:
@-moz-document domain(urbanterror.info)
{

/*
 * report bugs to jrandomnoob@gmail.com
 * or PM me on http://urbanterror.info
 */


/* --- generic b&w --- */

#forum_table td, .ipb_table td, .altrow, .row1, .row2, .off, .post, .post .citation, .post .quote, .tab_forums_posts li, .tab_forums_topics li, .post_block, .post_block h3, .entry-content, .posted_info, li.title i, p.edit, p.citation, div.blockquote, .account_userTag, .account_userRank, .newsTextBox, .darkerBox, #topicPreview, #topicPreview div, .tab_toggle_content {
	background-color: white ! important;
	color: black ! important;
	font-size: 12pt ! important;
	font-family: georgia ! important;
}

#forum_table td, .ipb_table td, .altrow, .row1, .row2, .off, .post, .post .citation, .post .quote, .tab_forums_posts li, .tab_forums_topics li {
	line-height: 1em ! important;
	text-align: left ! important;
}



/* --- links --- */

/*
 * Posts and other content only, to avoid
 * excessive uglification of the whole page
 */
#forum_table td a:link, .post a:link, .ipb_table td a:link, .ipb_table td h4 a:link, .post_block a:link, .post_body a:link, .darkerBox a:link, .tab_toggle_content a:link {
	color: blue ! important;
}

#forum_table td a:visited, .post a:visited, .ipb_table td a:visited, .ipb_table td h4 a:visited, .post_block a:visited, .post_body a:visited, .darkerBox a:visited, .tab_toggle_content a:visited {
	color: purple ! important;
}

.post a:link {
	text-decoration: underline ! important;
}

.post a:hover, .darkerBox a:hover {
	text-decoration: none ! important;
}



/* --- forum front page --- */

#forum_table td li, .desc, .darkerBox i, .darkerBox h2, .darkerBox h3, .darkerBox h4, .emptyBox, span.code {
	color: black ! important;
}

.main_topic_desc, form .desc {
	color: white ! important;
}


.topic_title, .ipb_table td h4 {
	font-size: 12pt ! important;
	font-weight: normal ! important;
}

/* To keep the thread titles from jumping around when you mouse over */
span.topic_preview a {
	display: block ! important;
	opacity: 0.7 ! important;
}

span.topic_preview a:hover {
	display: block ! important;
	opacity: 1.0 ! important;
}

.tab_toggle {
	background-color: white ! important;
	color: black ! important;
	border-color: black ! important;
}



/* --- ancillary pages (downloads, manual, profile etc) --- */

.status_profile_page {
	background: #141414 ! important;
}

.no-status, .general_box {
	color: #969696 ! important;
}

.status_profile_actions {
	width: auto ! important;
}

.tab_forums_posts .post_body, .tab_forums_topics .post_body {
	margin-left: 1em ! important;
}

div.link a:link {
	color: blue ! important;
}

div.link a:visited {
	color: purple ! important;
}

div.link.unread {
	background-color: white ! important;
	border-color: white ! important;
	font-weight: bold ! important;
}

tr.disabled, tr.forbidden {
	color: white ! important;
}

/* 0.5 opacity makes the links nigh-invisible */
tr.disabled {
	background-color: gray ! important;
	opacity: 1.0 ! important;
	filter: alpha(opacity = 100) ! important;
}



/* --- posts --- */

.account_FS, .account_FS .entry-content, .account_FS .posted_info, .account_FS h3, .account_FS .account_userTag, .newsTextBox {
	background-color: #ccceee ! important;
	color: #000444 ! important;
}

.post i, .post .bbcode, .userName, label {
	color: black ! important;
}

/*
 * Quick hack to avoid light text becoming unreadable
 * -- kills colored text though
 */
span[style] {
	color: #000000 ! important;
}

pre, p.citation {
	border: thin solid black ! important;
	border-bottom: none ! important;
}

.topic_summary .post_body, .general_box .post_body > .citation {
	margin-top: 1em ! important;
}

/*
 * Kills this silly tab that says "Resized to etc" that has
 * a tendency to cover quoted text...
 */
div.resized_img span {
	display: none ! important;
}

/* WhyTF was the lower border 6px? */
.resized_img img {
	border-width: 3px ! important;
}

/*
 * max-width on an image with size defined results in squishing,
 * but doing this will work exactly as intended
 */
img.resized {
	width: 98% ! important;
	height: 98% ! important;
}

/* Images in this class, however, have no size given */
.bbc_img {
	max-width: 99% ! important;
	max-height: 99% ! important;
}

.userRankFS {
	background-color: transparent ! important;
	color: white ! important;
}

ul.user_fields, li.title {
	font-size: 8pt ! important;
	color: black ! important;
}

/* "This post has been edited by" messages, code blocks & quote headers */
p.edit, pre, p.citation {
	border: thin solid black ! important;
}

/* Quote header beauty fix */
p.citation {
	border-bottom: none ! important;
}

.signature {
	color: black ! important;
}
}
