css:
@-moz-document domain("www.tuenti.com")
/* NEW tuenti.com CSS STYLE "darkTuenti" vF BY AITOR ESCOLAR (AUTLOS) .................  Twitter: @Aitor_EcHarley */
{

/*
.hover {
  background-color:#444 !important;
}
*/
/* General */

.layout-1, .layout-3, .layout-2, .layout-10 {
 background:transparent !important;
}

* {
 font-family: verdana !important;
 font-size:12px !important;
}

h2 {
 color: #DDD !important;
}

h3 {
 font-size:13px !important;
}
a {
 color:#5F9CE3 !important;
}

a:hover {
 color:#6FACF3 !important;
}

.commentForm .textInput.active .field{
 color:white !important;
}

/* Publicidad */
#home_unit_ads_container_id, #trigger-exclusive_sponsorships,#home_friends_importer, #canvas_friend_suggestions, #mobile_ad_unit{
 display:none !important;
}

/* cuerpo de toda la pagina */
body, .notranslate {
color: #DDD !important;
background: #828282 !important; /* Old browsers */
background: -moz-radial-gradient(center, ellipse cover,  #828282 0%, #333333 100%) !important; /* FF3.6+ */
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,#828282), color-stop(100%,#333333)) !important; /* Chrome,Safari4+ */
background: -webkit-radial-gradient(center, ellipse cover,  #828282 0%,#333333 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-radial-gradient(center, ellipse cover,  #828282 0%,#333333 100%) !important; /* Opera 12+ */
background: -ms-radial-gradient(center, ellipse cover,  #828282 0%,#333333 100%) !important; /* IE10+ */
background: radial-gradient(ellipse at center,  #828282 0%,#333333 100%) !important; /* W3C */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#828282', endColorstr='#333333',GradientType=1 ) !important; /* IE6-9 fallback on horizontal gradient */
}

body h5 {
 color: #DDD !important;
}

#content #home {
 background-color:none !important;
}

.overlayActions {
 background-color:#444 !important;
}
/* Header */
.header { 
  box-shadow: 0 0 14px black !important;  
  background-image: none !important; 
  border: none !important; 
}

.header {
background: #4c4c4c !important; /* Old browsers */
background: -moz-linear-gradient(top,  #4c4c4c 0%, #383838 32%, #3b3b3b 69%, #4f4f4f 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#4c4c4c), color-stop(32%,#383838), color-stop(69%,#2b2b2b), color-stop(100%,#4f4f4f)) !important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #4c4c4c 0%, #383838 32%, #3b3b3b 69%, #4f4f4f 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #4c4c4c 0%, #383838 32%, #3b3b3b 69%, #4f4f4f 100%) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #4c4c4c 0%, #383838 32%, #3b3b3b 69%, #4f4f4f 100%) !important; /* IE10+ */
background: linear-gradient(to bottom,  #4c4c4c 0%, #383838 32%, #3b3b3b 69%, #4f4f4f 100%) !important; /* W3C */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#4c4c4c', endColorstr='#4f4f4f',GradientType=0 ) !important; /* IE6-9 */

}

#sections a:hover , #more_options_button_header:hover { 
  background-color: transparent !important; 
  background-image: none !important; 
  border-bottom: none !important; 
  /*text-shadow: 0 0 1px #90C4EC !important; */
}

#sections .active { 
  background-color: transparent !important; 
  border-bottom: none !important;
  background-image: none !important; 
  color: #4778AF !important; 
}
a.active  { 
  background-color: transparent !important; 
  background-image:none !important; 
}


/****  CHAT  ****/

.me, .dck-bubble {
  background: none !important;
}

#chat_home_module{
 background-color: transparent !important;
 color: #ffffff !important;
}

#chat_header_module, #chat_dock_viewport{
 background-image: -webkit-linear-gradient(top,#555,#444) !important;
 background-image: -moz-linear-gradient(top,#555,#444) !important;
 background-image: -ms-linear-gradient(top,#555,#444) !important;
 background-image: linear-gradient(top,555,#444) !important;
}


#chat_header_menu .overlayInner{
 background: #444 !important;
}
#overlayBody {
 font-size:13px !important;
 color:white !important;
}
.box, .dck-bubble, .overlayHeader, .dck-header{
  background-color: #444 !important;
}

.dck-flow p, .dck-dialog p, .dck-flow a, .dck-dialog a{
 font-size:13px !important;
 color:#ffffff !important;
 
}
#chat_dock .dck-flow, #chat_dock .dck-writer, #chat_dock .messageInfo {
 background-color: #555 !important;
}

#chat_dock .textfield {
 background-color: #444 !important;
} 

.dck-room-name {
 color:#FFF !important;
}

#chat_header_roster_footer {
 background-color: #444 !important;
}
.txf-target, .dck-fld-target {
 color:#DDD !important;
}

/* Bloqueo y eventos */
#participants .inlineSetting {
  background:none !important;
  color:#444 !important;
}
#participants .setting {
  color:#444 !important;
  background:#DDD !important;
}


#non_blocked_friends_list, #blocked_friends_list, #event_not_invited_friends_list, #invited_friends_list{
  background-color:#333 !important;
}

#non_blocked_friends_list .hover, #blocked_friends_list .hover, #event_not_invited_friends_list .hover, #invited_friends_list .hover{
  background-color:#444 !important;
  background:#444 !important;
  color:#DDD !important;
}

.lightbox {
 background-color:#444 !important;
 color:#DDD !important;
}



/******** INDEX  ********/

.left{
 background-color: #444 !important;
 border: 1px #878787 solid !important;
 border-radius: 1px !important;
}

#recent_pages h2, .views, .views strong{
 color:#FFFFFF !important;
}


#home_main_info, .body {
 background-color: #444 !important;
 border: none !important;
}

.body {
 box-shadow:none !important;
}


/* centro de la pagina */
.current {
  font-family: verdana !important;
  font-size: 12px !important;

}

.status {
 background-color:#DDDDDD !important;
 color:#000000 !important;
 border-radius: 5px !important;
}

#home_feed_list .textInput{
 border-radius: 3px !important;
 color: white !important;
 box-shadow: inset 1px 1px 0 0 222222 !important;
}

.userName {
  background-color:transparent !important;
}
.commentEntry {
  color:#ffffff !important;
}

/******** PERFIL ********/
#show_blog_entry {
  color:#ffffff !important;
}

.info{
 font-size:12px !important;
}
.text{
  color:#ffffff !important;
  font-size:13px !important;
}

.formBox {
 background-color:#444 !important;
}

.replyText p{
  font-size:12px !important;
  color:#DDD !important;
}

.statusWall {
  background-color: #444 !important;
}

/******* ALBUMES *******/
.layout-10 {
  background-color:#444 !important;
}
#photo_albums h3 {
  text-shadow: none !important;
  color: #FFF !important;
}

#albums .main{
  width:741px !important;
}

#albums .timeSelector{
  background-color:#555 !important;
  box-shadow:1px 1px 1px black !important;
}

#albums .breadcrumbs {
  background-color:#444444 !important;
}


/******* FOTO *******/
#photo_title {
  color:#FFFFFF !important;
}
#photo_details, .mainActions, #tag_entries li {
  background-color:#444 !important;
}

.nomola, .molaLink {
  text-shadow:none !important;
}

/******* OTROS *******/
#save_gif{
  width:0px !important;
  height:0px !important;
}
.searchResults, .content {
  color:#ffffff !important;
}

/* Mensajes Privados */
.layout-2 .main, .mainContent {
  padding-left:10px !important;
}

.wall .item {
  background-color:#444 !important;
}

.mod.menu li a.active span {
  background-color:#BEBEBE !important;
}


#mail_box_body .tdAuthor.hover {
  background-color:#BEBEBE !important;
}

#mail_box_body .hover {
  background-color:#BEBEBE !important;
  color:#000 !important;
}


/* otras pestañas */
#games_canvas .nSubHeader, #videos_canvas .nSubHeader, #internal_canvas_home .nSubHeader {
  background: none !important;
}
.gameFilterBox{
  background-color:#444 !important;
}
#wall_body .feedBackBody {
  background-color:#444 !important;
}

#request_management_canvas .filterMenu{
  background-color:#444 !important;
}

#page_request_list .request, #event_request_list .request  {
  background: none !important;
  background-color: #444 !important; 
  border-bottom: none !important;

}
#page_request_list .request p, #event_request_list .request p{
   color: #DDD !important;
}

#page_request_list .feedBackBody, #event_request_list .feedBackBody{
  background-color: #444 !important;
}


#photo_widget .buttonBar {
  background: #444 !important;
}

#photo_widget .albumName {
  color: #DDD !important;
}

#wall_posts_content .status {
  background-color: #444 !important;
  color: #DDD !important;
}

/* Gente */
#multiitemsearch .right {
  background-color:#444 !important;
}

}
