css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("www.tumblr.com") {



}



#dashboard_index, #dashboard_inbox, #main_help, #dashboard_settings, #dashboard_drafts, #dashboard_followers, #dashboard_submissions, #dashboard_post_queue, #tagged_display, #dashboard_following, #dashboard_likes, #dashboard_index #container, #main_help #container, #dashboard_inbox #container, #dashboard_settings #container, #dashboard_drafts #container, #dashboard_followers #container, #dashboard_submissions #container, #dashboard_post_queue #container, #tagged_display #container, #dashboard_following #container, #dashboard_likes #container, #dashboard_index #container #header_container, #dashboard_inbox #container #header_container, #main_help #container #header_container, #dashboard_settings #container #header_container, #dashboard_drafts #container #header_container, #dashboard_followers #container #header_container, #dashboard_submissions #container #header_container, #dashboard_post_queue #container #header_container, #tagged_display #container #header_container, #dashboard_following #container #header_container, #dashboard_likes #container #header_container, #dashboard_directory, #dashboard_directory #container, #dashboard_directory #container #header_container, #dashboard_lookup_contacts, #dashboard_lookup_contacts #container, #dashboard_lookup_contacts #container #header_container, #dashboard_tumblelog_settings, #dashboard_tumblelog_settings #container, #dashboard_tumblelog_settings #container #header_container{

background: #929292 url('http://static.tumblr.com/eea1p82/QsMm9ow09/kenny-dash-2.png') no-repeat fixed left bottom !important;

}



#dashboard_index #container, #dashboard_inbox #container, #dashboard_drafts #container, #dashboard_followers #container, #dashboard_submissions #container, #dashboard_index #container div.l-content, #dashboard_inbox #container div.l-content, #dashboard_drafts #container div.l-content, #dashboard_followers #container div.l-content, #dashboard_submissions #container div.l-content, #dashboard_post_queue #container div.l-content, #tagged_display #container, #tagged_display #container div.l-content, #dashboard_following #container, #dashboard_following #container div.l-content, #dashboard_likes #container, #dashboard_likes #container div.l-content, #dashboard_tumblelog_settings #container, #dashboard_tumblelog_settings #container #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background-color:rgba(108, 153, 157, 0.01) !important;

}



#posts .post .permalink{

background:none !important;

}



#dashboard_index .selection_nipple, #dashboard_inbox .selection_nipple{

border-bottom:12px solid rgba(108, 153, 157, 0.01) !important;

}



.nipple{

border-right:7px solid #1d1d1d !important;

}



ul.controls_section, input#search_query{

color:#efefef !important;

background-color:#1d1d1d !important;

}



#next_page_link, #previous_page_link{

color:#efefef !important;

background-color:#1d1d1d !important;

border:1px solid rgba(0,0,0,0.18) !important;

}



li.notification, #posts .notification{

color:#efefef !important;

background-color:#1d1d1d !important;

border:none !important;

margin-bottom:2px !important;

border-radius:5px !important;

-moz-border-radius:5px !important;

-webkit-border-radius:5px !important;

-o-border-radius:5px !important;

-ms-border-radius:5px !important;

}



li.notification a{

color:#efefef !important;

underline:none !important;

border:none !important;

}



li.notification a:hover{

color:rgba(239, 239, 239, 0.9) !important;

}



#posts .notification .notification_type_icon.like_icon{

background:url('http://static.tumblr.com/cxtftev/VjSm9ed33/dashboard-like-icon.gif') no-repeat scroll 0px 0px transparent !important;

}



#posts .notification .notification_type_icon.reblog_icon{

background:url('http://static.tumblr.com/cxtftev/jTFm9ecbz/dashboard-reblog-icon.gif') no-repeat scroll 0px 0px transparent !important;

height:15px;

}



#posts .notification .notification_type_icon.follow_icon{

background:url('http://static.tumblr.com/cxtftev/Ckem9ecr3/dashboard-follow-icon.gif') no-repeat scroll 0px 0px transparent !important;

}



a.avatar_frame, img.avatar{

border-radius:5px !important;

-moz-border-radius:5px !important;

-webkit-border-radius:5px !important;

-o-border-radius:5px !important;

-ms-border-radius:5px !important;

}



#dashboard_inbox #container div.l-content #right_column .send_fan_mail{

color:#efefef !important;

background-color:#1d1d1d !important;

background-image:none !important;

box-shadow:0px 1px 0px 0px #6c999d inset, 0px 1px 6px 0px #6c999d !important;

border:1px solid rgba(0,0,0,0.18) !important;

}



#dashboard_index #container div.l-content #right_column, #dashboard_inbox #container div.l-content #right_column{

background-image:none !important;

box-shadow:none !important;

border:none !important;	

}



#dashboard_index #container div.l-content #left_column #dashboard_controls a, #dashboard_index #container div.l-content #left_column #dashboard_controls span, #search_controls_button, #right_column .controls_section li .blog small{

color:#efefef !important;

}



.hide_overflow, ul.controls_section a, #dashboard_index #container div.l-content #left_column #dashboard_controls .dashboard_controls_arrow_right{

color:#efefef !important;

}



#dashboard_post_queue #container div.l-content #left_column ol#posts .no_posts_found h1.dashboard_header{

color:#efefef !important;

}



#footer, #footer a{

color:#efefef !important;

}
