css:
@-moz-document domain("twitter.com") {
  .js-machine-translated-tweet-container {
    display: none !important;
  }

  .bio {
    font-style: normal !important;
  }

  .js-wtf-module .js-recommended-followers, .discover-nav .avatar-row, .WhoToFollow-users {
    display: none !important;
  }

  .js-similar-to-module .flex-module {
    display: none !important;
  }
  .js-similar-to-module .list-link {
    -webkit-border-bottom-right-radius: 5px;
    -webkit-border-bottom-left-radius: 5px;
    -moz-border-radius-bottomright: 5px;
    -moz-border-radius-bottomleft: 5px;
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
  }

  .js-tweet-text {
    white-space: pre-line;
  }

  .tweet {
    cursor: inherit;
  }

  div[id*="promoted"], .promoted-tweet {
    display: none;
  }

  .js-wtf-module .flex-module {
    -webkit-border-bottom-right-radius: 5px;
    -webkit-border-bottom-left-radius: 5px;
    -moz-border-radius-bottomright: 5px;
    -moz-border-radius-bottomleft: 5px;
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
  }
  .flex-module-header {
    margin-bottom: 0;
  }
  .wtf-module .import-prompt {
    display: none;
  }

  .TweetTextSize {
    font-size: 14px !important;
    line-height: 22px !important;
  }

  .is-pinned {
    border-color: rgb(0, 119, 170);
    z-index: 1;
  }
}
