@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("metalstorm.ee") {

/*Darker Album Text in Upcoming*/
TBODY > TR > TD.content > TABLE > TBODY > TR > TD.windowbg > TABLE > TBODY > TR > TD
{
  color:#000000 !important;
}


/*visited text*/
a:visited
{
  color:#D8710A !important;
}

/*hovertext*/
a:hover
{
  color:#D80000 !important;
}


/*Upcoming Page*/
.windowbg2,.windowbg
{
  background:#FFFFFF !important;
  color:#000000 !important;
}




html,table,tr,p,body,div
{
  background-color:#FFFFFF!important;
  color:#000000!important;
  font-family:Calibri,Arial !important;
  font-size:14px;
  border-color:black !important; 
  border:1px !important;
}


FONT
{
color:#000000;
background-color:#FFFFFF;

}


DIV > TABLE > TBODY > TR > TD,a:link
{
  color:#000000 !important;
  background-color:#FFFFFF !important;
}

a:hover
{
  color:#FF9933;
}

SPAN.title,span.megatitle
{
  color:#000000 !important;
}



td.page_title,td.menu_title,td.menu_items,element.style,td.left,td.right
{
  background:#FFFFFF !important;
  color:#000000 !important;
}


/*Cannot seem to display the MetalStorm Logo Header
HTML > BODY > TABLE > TBODY > TR > TD
{
  background:url("http://www.metalstorm.ee/images/header.jpg") !important;
}
*/


}
