css:
body
{
background-color:#7d6948;
}

div.userbar a:link
{
color:#dbdbad !important;
}
div.userbar a:visited 
{
color:#dbdbad !important;
}
div.userbar a:hover 
{
color:#ffffff !important;
}
div.userbar a:active 
{
color:#f9de27 !important;
}

div.menubar{
display:block !important;
margin-bottom:14px !important;
padding: 4px!important;
box-shadow: 0px 4px 10px 0px #111 !important;
border-radius: 10px !important;
background-color:black;
}

span.menubar
{
top:15px!important;
left:-4px !important;
}

span.menubar, span.menubar a, span.menubar a:link, span.menubar a:visited
{
background-color:black !important;
color:rgba(255,255,255, 0.9) !important;
}

div.menubar a, div.menubar a:link, div.menubar a:visited
{
color:#dbdbad !important;
}


div.menubar a:hover, div.menubar a:link:hover, div.menubar a:visited:hover, span.menubar a:hover
{
color:#ffffff !important;
}

div.infobar 
{
background: #000000;
color: #dbdbad !important;
padding: 1px 3px;
text-align: center;
}

div.infobar a, div.infobar a:link, div.infobar a:visited
{
color:#dbdbad !important;
}



span.menubar a:link:hover, span.menubar a:visited:hover{
color:#ffffff !important;
}

table.grid tr th 
{
background: #000000;
text-align: center;
color: #dbdbad;
}

div.menubar, div.userbar 
{
color: #000000;
padding: 1px 3px;
text-align: center;
}


table.grid tr td, table.grid tr th 
{
border: 2px solid #7d6948;
border-width: 0 2px 2px 0;
}






.userbar
{
	background-color: rgba(0, 0, 0, 0.9) !important;
	border-top-left-radius: 20px !important;
	border-top-right-radius: 20px !important;
        box-shadow: 0px 0px 0px 0px #111 !important;

}

.message-top
{
	background-color: rgba(250, 37, 0, 0.7) !important;
        border-top-left-radius: 9px !important;
	border-top-right-radius: 9px !important;
        border-bottom-left-radius: 9px !important;
	border-bottom-right-radius: 9px !important;

        box-shadow: 0px 2px 3px 0px #111 !important;
}


.message
{
	background-color: rgba(226, 221, 181, 0.9) !important;
        border-top-left-radius: 5px !important;
	border-top-right-radius: 5px !important;
        border-bottom-left-radius: 5px !important;
	border-bottom-right-radius: 5px !important;
}


.userpic
{
	background-color: rgba(0, 0, 0, 0.1) !important;
}


.quickpost
{
	background-color: rgba(0, 0, 0, 0.1) !important;
}
.quickpost-canvas
{
	background-color: rgba(85, 174, 58, 0.5) !important;
}


a:link
{
	color:#000000 !important;
}
a:visited
{
	color:#3b003b !important;
}
a:hover
{
	color:#ff2500 !important;
}
a:active
{
	color:#ffffff !important; 
}


h1
{
	color:#ffffff !important;
        padding-top: 5px;
	padding-bottom: 3px;
        text-shadow: 2px 2px 2px black;
}

table.message-body td.userpic 
{
border-left: 1px solid #7d6948;
padding: 1px;
vertical-align: bottom;
}








html > body > div > table > tbody > tr > td > table > tbody > tr > th
{
	background-color: rgba(250, 37, 0, 0.7) !important;
	color: #191919 !important;
}


html > body > div > table > tbody > tr > td > table > tbody > tr > td
{
	background-color: rgba(173, 219, 173, 0.9) !important;
}


html > body > div > table > tbody > tr > td
{
	background-color: rgba(226, 221, 181, 1) !important;
}

.fl, .fr { float: none !important; }

.fr { opacity:0.5; }
