css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com") {

#content-container #content, #watch-video-container,
#watch-sidebar, #ppv-container,
#watch-sidebar .yt-vertical-rule-main,
#watch-sidebar .yt-vertical-rule-corner-top,
#watch-sidebar .yt-vertical-rule-corner-bottom {
    background: none transparent !important;
}

#watch-context-container, #branded-playlist-module,
.watch-sidebar-section .watch-sidebar-head,
.watch-sidebar-section .yt-horizontal-rule {
    display: none !important;
}

#watch-channel-discoverbox {
    background: #555 !important;
    border: 1px solid #000 !important;
    border-radius: 3px !important;
    box-shadow: none !important;
}

#watch-channel-discoverbox .yt-uix-slider-num,
#watch-channel-discoverbox .yt-uix-slider-next,
#watch-channel-discoverbox .yt-uix-slider-prev {
    background: #FFF !important;
    box-shadow: none !important;
    color: #333 !important;
    text-shadow: none !important;
}

#watch-channel-discoverbox .yt-uix-slider-num-current {
    background: #DDD !important;
}
#watch-channel-discoverbox .yt-uix-slider-num,
#watch-channel-discoverbox .yt-uix-slider-next,
#watch-channel-discoverbox .yt-uix-slider-prev {
    background: #FFF !important;
    box-shadow: none !important;
    color: #333 !important;
    text-shadow: none !important;
}

#watch-channel-discoverbox .yt-uix-slider-num-current {
    background: #DDD !important;
}

#watch-video.medium {
    background: #555 !important;
    border: 1px solid #000 !important;
    border-radius: 3px !important;
    padding: 5px 0 !important;
}

#watch-video.large {
    background: #555 !important;
    border: 1px solid #000 !important;
    border-radius: 3px !important;
    padding: 10px !important;
}

.watch-sidebar-section {
    background: #555 !important;
    border: 1px solid #000 !important;
    border-radius: 3px !important;
    padding: 5px 3px !important;
}

.watch-sidebar-head, .yt-uix-slider-title *,
#watch-sidebar .video-list-item .title,
#watch-sidebar .video-list-item .stat,
#watch-sidebar .video-list-item .description,
#watch-sidebar .video-list-item .yt-badge-std,
#watch-channel-discoverbox .yt-uix-slider-slide-item .title,
#watch-channel-discoverbox .yt-uix-slider-slide-item .stat {
    color: #DDD !important;
}

#eow-title, #eow-title a,
#watch-sidebar .video-list-item:hover .title,
#watch-sidebar .video-list-item:hover .stat,
#watch-sidebar .video-list-item:hover .description,
#watch-sidebar .video-list-item:hover .yt-badge-std,
#watch-channel-discoverbox .yt-uix-slider-slide-item:hover .title,
#watch-channel-discoverbox .yt-uix-slider-slide-item:hover .stat,
#watch-channel-discoverbox .yt-uix-slider-slide-item .yt-tile-visible .title,
#watch-channel-discoverbox .yt-uix-slider-slide-item .yt-tile-visible .stat {
    color: #333 !important;
}

#watch-channel-discoverbox .yt-uix-slider-slide-item * {
    box-shadow: none !important;
}

#watch-sidebar .video-list-item:hover .related-video {
    background: #EEE !important;
    border: 1px solid #000 !important;
}

#watch-sidebar .video-list-item .yt-badge-std {
    background: none transparent !important;
    box-shadow: none !important;
    font-size: 9px !important;
    text-transform: lowercase !important;
}

#watch-sidebar .video-list-item .yt-badge-std:before {
    content: "\005B" !important;
}

#watch-sidebar .video-list-item .yt-badge-std:after {
    content: "\005D" !important;
}

#watch-more-related-button,
#watch-more-related-button:hover {
    background: transparent none !important;
    border: none !important;
    box-shadow: none !important;
    color: #DDD !important;
    text-shadow: none !important;
}

#watch-sidebar .watch-sidebar-foot {
    margin: 0 !important;
}
}
