css:
@-moz-document url-prefix("http://egza.org/bird/") {
#tw.tw.tw-parent {
    top:74px !important;
    }
#misc {
    margin-right:400px !important;
}

#menu a.new {
background-color:rgba(7, 173, 255, 0.8);

background-image: -moz-radial-gradient(center bottom, ellipse farthest-side, rgba(7, 173, 255, 0.8), #333333 100%);

background-image: -webkit-radial-gradient(center bottom, ellipse farthest-side, rgba(7, 173, 255, 0.8), #333333 100%);

background-image: -o-radial-gradient(center bottom, ellipse farthest-side, rgba(7, 173, 255, 0.8), #333333 100%);

background-image: -ms-radial-gradient(center bottom, ellipse farthest-side, rgba(7, 173, 255, 0.8), #333333 100%);

background-image: radial-gradient(center bottom, ellipse farthest-side, rgba(7, 173, 255, 0.8), #333333 100%)
}

body {
     padding:0px !important;
    margin:0px !important;
  background-image: url(http://behroozina.xp3.biz/003-subtle-light-pattern-background-texture.jpg) !important;
 }
#counter-div {
    top:17px !important;

}
#counter-p1  {
    visibility:hidden !important;
    
}
#counter-p2  {
    visibility:hidden !important;
}
#counter {
    font-family:Tahoma !important;
	font-style:normal !important; 
	padding:0px !important;
	font-size:30pt !important;
	color:#ccc !important;
	background-color:none !important;
	width:0px !important;
	}
#menu {
	background-color: #333333  !important;
	top:0px  !important;
	position:relative  !important;
	padding:0px  !important;
	width: 100%  !important;
	text-align:center  !important;
}

#fst {
    font-size:12pt !important;
	top:30px  !important;
	height:40px  !important;
	width: 100% !important;
	margin:0px !important;
	padding: 7px 20px 0px 0px !important;
	background:none !important;
	padding-right:20px  !important;
}

#geotag {
	visibility: hidden  !important;
}

#option {
	visibility: hidden  !important;
	position: realative  !important;
}
.uicon:hover {
    opacity:0.6;
}
.uicon {
	padding:0px  !important;
	-webkit-border-radius: 8px !important;
	-moz-border-radius: 4px !important;
	border-radius: 7px !important;
	-webkit-box-shadow: 1px 1px 3px rgba(52, 50, 50, 0.65) !important;
	-moz-box-shadow:    1px 1px 3px rgba(52, 50, 50, 0.65) !important;
	box-shadow:         1px 1px 3px rgba(52, 50, 50, 0.65) !important;
	margin-right:-55px !important;
	margin-top:-2px !important;
}
#tw > div {
	
	font-family: tahoma !important;
	border:0 !important; 
}
#tw div:first-child > div {
top:-20px !important;
  border-color:#000 !important;
  background-color:#FAFAFA !important;
}

.tw-parent > div > div {
  
width:240px !important;
float:right !important;
border: 1px solid #999 !important;
margin:2px !important;

margin-right:55px !important;
padding:2px !important;
}

span.uid ,#tw a{
font-weight:bold !important;
text-decoration:none !important;
text-shadow: 0px 1px 0px rgb(252, 249, 238), 0px 2px 0px rgba(0, 0, 0, 0.15) !important;
color: #666 !important;
}

.utils {
position:relative !important;
float:left !important;
text-align:center !important;
}
.utils a {
color:#dedede !important;
}

.popup_menu {
border-radius:5px !important;}

.popup_menu a:hover {
background-color:69f !important;}

#control {
  
   height: 30px  !important;
   background-color:#000  !important;
}
#menu a {
    display: inline-block !important;
    font-size: 12px !important;
    height: 30px !important;
    line-height: 30px !important;
    border: 0px none !important;
    text-decoration: none !important;
    -moz-transition:all 0.5s !important;
    transition:all 0.5s !important;
}
}
