css:
@-moz-document domain("7chan.org") {
div.navbar {color: #fff !important;}
div.replymode {color: #666 !important;}
a {color: #f00 !important;}
#posting_form label {color: #666 !important;}
input, textarea {color: #ff7 !important;}
#rules {color: #fff !important;}
.post {color: #fff !important;}
.post_header {color: #666 !important;}
.postername {color: #0a0 !important;}
html, body, body > footer {color: #fff !important;}
section#recent {color: #fff !important;}
section#news {color: #aaa !important;}
h4 {background: #222 !important;}
section#boardlist {color: #fff !important;}


div.navbar {background: none #333 !important;}
html, body, body > footer {background: none #000 !important;}
div.replymode {background: #222 !important;}
#posting_form label {background: #222 !important;}
input, textarea {background: #000 !important;}
#rules ul#tabs {background: #222 !important;}
#rules ul#tabs li.selected {background: #444 !important;}
#rules {background: #333 !important;}
.post {background: #222 !important;}
.post_header {background: #333 !important;}
section#recent {background: #444 !important;}
section#recent section#posts li:nth-child(even) {background: #222 !important;}
section#news {background: #000 !important;}
section#news header ul li.selected {background: #222 !important}
section#news header ul li {background: #444 !important;}
section#news header {background: #666 !important}
h4 {background: #777 !important;}
section#boardlist {background: #111 !important;}
}
