css:
@-moz-document domain('gamer.ru'), domain('геймер.рф') {
#main .teaser {display: none;}
.branding-link {display: none;}
footer {z-index: 5;}
.wrapper {z-index: 10;}
#frame div.back-top {background: url(http://asnow.panweb.com/gallery:top_clean.jpg) 50% 0 !important; height: 1081px !important; z-index: 3; }
#frame .back-top:before { content: ""; display: block; background: url(http://asnow.panweb.com/gallery:top_stripe.jpg); height: 149px; width: 887px; margin: 0 auto; }
#frame .back-top:after { content: ""; display: block; background: url(http://asnow.panweb.com/gallery:top_stripe.jpg) no-repeat 100% 0; height: 149px; width: 908px; margin: -149px auto; }
#frame .back-bottom { background: url(http://asnow.panweb.com/gallery:center.jpg) 50% 0 !important;z-index: 2; }
#frame .back-center {background: url(http://asnow.panweb.com/gallery:center.jpg) 50% 0 !important;top: 1110px !important;z-index: 1;}
#frame {background: #2B3F28 !important;}
.inner.gamazavr {display: none;}
#main .block aside .inner.top-list {margin-top: 0px;}
#main .main-banner-double {display: none;}
#main .block .item.in-post .content > p {line-height: 1.3em; margin: 1em 0 1em 0;}
#main .block .comm-in-post ul.comments-in-post > li {text-align: justify;}
#comments_list {font-size: 1.1em;}
#main .block .item, #main .wide {background: #F0EEDF;}
#frame .branding-link-bottom {display: none;}
#frame #logo {margin-top: 15px;}
#main a.game-box {display: none;}

/* inner*/
#main .head-post .top-line {background: #DDD7BD;}
#main .head-post ul.action-post li a.action, body div.action-post a.action, #main .block .item.comm-in-post ul.action-post li a.action { background: #A34424; color: #fff; padding: 5px 6px 4px 6px; }
body div.action-post {border: 1px solid #BBB;}
#main ul.action-post li a.action, body div.action-post a.action, #main .block .item.comm-in-post ul.action-post li a.action {background: #A34424; color: #fff; padding: 5px 6px 4px 6px;}
body div.action-post a.action span, #main .in-post .content .vote label.active, #main .comm-in-post .content .form .label label.active, #main .in-post .content .vote-result ul li.active span, p.green-comment {color: #fff}
#main .block .comm-in-post ul.comments-in-post li div.wrote {padding: 7px 9px;}
#main .block .comm-in-post ul.comments-in-post li div.wrote p {line-height: 1.3em;}
#main .block .comm-in-post ul.comments-in-post li div.wrote .image.error {padding: 1em;color: red;}
.pagination .pages a:hover { background: #A59B81; }

/*scope end*/
}
