css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('twitter.com') {
    .js-action-del b {
        font-size: 0;   
    }    
    .js-action-del b:after {
        content: "Eff this!";    
        font-size: 12px;                
        position: relative;             
        left: -1px;      
    }
    .sm-trash{                            
        background: none !important;    
        width: 72px !important;         
    }                                   
    .sm-trash:after {                     
        content: "(╯°□°)╯︵ ┻━┻";              
        font-style: normal;             
        position: relative;            
        top:-3px;                       
    }
}
