css:
@-moz-document url("https://www.tumblr.com/preferences"), url-prefix("http://www.tumblr.com/") , url-prefix("https://www.tumblr.com/") {
.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){background-image:url("http://i50.tinypic.com/15qxc3p.jpg")!important;background-repeat:no-repeat!important;width:18px!important;height:15px!important;margin-top:-15px!important}.selection_nipple.white{background-position:0 0!important;width:18px!important;height:15px!important}.selection_nipple:not([class="selection_nipple white"]){border-bottom:solid 11px rgba(0,0,0,0)!important}

#inbox_button.tab.iconic.selected .selection_nipple:not([class="selection_nipple white"]){margin-left:-9px!important;left: 0px !important;}

#home_button.tab.iconic.selected .selection_nipple:not([class="selection_nipple white"]){margin-left:-10px!important;left: 0px !important;}




li.selected, li.recessed, a.active, div.post_filter_tabs a {
  background: none !important;
}



.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}


.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/tagged"), url-prefix("http://www.tumblr.com/inbox") , url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/tagged"), url-prefix("https://www.tumblr.com/inbox") {
.arrow {
 display: none !important;
}

#range_controls h2.heading.arrow{
  display:inherit !important;
}


#posts .post .post_permalink {
width: 30px !important;
height: 30px !important;
background: url('http://i49.tinypic.com/34dlnvb.png')!important;
background-size: 100% 100% !important;
position: absolute !important;
top: 13px !important;
right: -13px !important;
}

#posts .post{
 background: #ffffff; }
.post_avatar:after {
  display: none !important;
}
.tumblelog_menu_popover {
left: 40% !important;
}

.post_avatar_link {
  width: 48px !important;
  height: 48px !important;
position: absolute !important;
left: 13px !important;
top: 13px !important;
  background-size: 48px 48px !important;
  border-radius: 60px !important;
  background-repeat: no-repeat !important;
  background-clip: padding-box !important;
}
.post_avatar_link .blog_avatar{
top: -1px !important;
left: 1px !important;
}
.plexi.show.color{
background: rgba(0,0,0,0.9)!important;}

.post_avatar .post_sub_avatar {
position: absolute !important;
left: 45px !important;
top: 45px !important;
border: solid 2px rgba(0,0,0, 1) !important;
  border-radius: 25px !important;
}

.post_avatar{
background-image: url('http://i45.tinypic.com/2gvj20z.png') ;
width: 92px !important;
height: 75px !important;
margin-left: -11px !important;
background-color: rgba(0,0,0,0) !important;
}

#posts .post.same_user_as_last .post_avatar, #posts .post.same_user_as_last .post_avatar_link, #posts .post.same_user_as_last .sub_avatar, #posts .post.same_user_as_last .user_menu_info_button {
 display:inherit !important;
}

#posts .notification a:not(.avatar_frame) {
 border-bottom: 1px solid #626262 !important;}
#posts .notification .nipple.border, #posts .notification.alt .nipple.border {
 border-right: 7px solid transparent !important;
 border-left: 15px solid #626262 !important;
 left:-20px !important;
 top:12px !important;}
#posts .notification .nipple, #posts .notification.alt .nipple  {
 border-right: 6px transparent !important;}
#posts .notification .avatar_frame {
border: 2px solid #626262 !important;
background-color: #000000 !important;
border-radius: 60px !important;}
#posts .notification a:not(.avatar_frame) {
text-decoration: none !important;
border-bottom: none !important;}

#posts .notification .avatar{
  border-radius: 60px !important;}
}

@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/tagged") , url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/tagged") {
img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],
.new_post_label_icon {
    opacity: 0 !important;
}



#new_post_label_text,
#new_post_label_photo,
#new_post_label_quote,
#new_post_label_link,
#new_post_label_chat,
#new_post_label_audio,
#new_post_label_video {
  background: rgba(255,255,255, 1.0) url("http://i46.tinypic.com/25jy439.png") no-repeat !important;
}


#posts .post.new_post .new_post_label{
padding-top: 70px !important;
padding-bottom: 0px !important;
font-family: "Courier", Courier New,Georgia,Serif;
font-weight: bold !important;}

#new_post_label_text {
  background-position: 2px 0px !important;
  border-top-left-radius: 8px !important;
  border-bottom-left-radius: 8px !important;
}
#new_post_label_photo {
  background-position: -73px -1px !important;
}
#new_post_label_quote {
  background-position: -148px 0px !important;
}
#new_post_label_link {
  background-position: -223px 0px !important;
}
#new_post_label_chat {
  background-position: -298px 0px !important;
}
#new_post_label_audio {
  background-position: -373px 0px !important;
}
#new_post_label_video {
  background-position: -448px 0px !important;
  border-top-right-radius: 8px !important;
  border-bottom-right-radius: 8px !important;
}




#new_post_label_text:hover,
#new_post_label_photo:hover,
#new_post_label_quote:hover,
#new_post_label_link:hover,
#new_post_label_chat:hover,
#new_post_label_audio:hover,
#new_post_label_video:hover {
  color: black !important;
  font-weight: bold !important;
}
 


#new_post_label_text:hover {
  background-position: 2px -95px !important;
}
#new_post_label_photo:hover {
  background-position: -73px -95px !important;
}
#new_post_label_quote:hover {
  background-position: -148px -95px !important;
}
#new_post_label_link:hover {
  background-position: -223px -96px !important;
}
#new_post_label_chat:hover {
  background-position: -298px -95px !important;
}
#new_post_label_audio:hover {
  background-position: -373px -95px !important;
}
#new_post_label_video:hover {
  background-position: -448px -97px !important;
}
}

@-moz-document url("http://www.tumblr.com/help"), url-prefix("https://www.tumblr.com/settings"), regexp("^http://www.tumblr.com/blog/[A-Za-z0-9._%+-]+/settings") , url("https://www.tumblr.com/help") {
.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){background-image:url("http://i50.tinypic.com/15qxc3p.jpg")!important;background-repeat:no-repeat!important;width:30px!important;height:30px!important;margin-top:-15px!important}.selection_nipple.white{background-position:0 0!important;width:30px!important;height:30px!important}.selection_nipple:not([class="selection_nipple white"]){border-bottom:solid 11px rgba(0,0,0,0)!important}
}
