css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace html url(http://www.w3.org/1999/xhtml);
@-moz-document 
  url(chrome://browser/content/browser.xul),
  url(chrome://firebug/content/firefox/firebugFrame.xul)
{
  #firebug-button,
  #menu_firebug, #appmenu_firebug, #menu_webDeveloper_firebug {
    list-style-image: url(http://foxkeh.jp/favicon.ico) !important;
  }
  #fbFirebugMenu > image {
    width:20px;
    height:20px;
    padding-left:20px;
    background: url(http://foxkeh.jp/favicon.ico) no-repeat !important;
  }
}
@-moz-document
  url(chrome://firebug/content/panel.html)
{
  html|*.panelNode {
    background: -moz-linear-gradient(90deg, rgba(255,255,255,0.6), rgba(255,255,255,0.6)),
                url(http://r.dynamis.jp/foxkeh.png), white !important;
    background: linear-gradient(180deg, rgba(255,255,255,.6), rgba(255,255,255,.6)),
                url(http://r.dynamis.jp/foxkeh.png), white !important;
  }
  html|*.disabledPanelBox {
    background: url(http://r.dynamis.jp/foxkehicon32.png) no-repeat 30px 30px,
                -moz-linear-gradient(90deg, rgba(255,255,255,0.6), rgba(255,255,255,0.6)),
                url(http://r.dynamis.jp/foxkeh.png) !important;
    background: url(http://r.dynamis.jp/foxkehicon32.png) no-repeat 30px 30px,
                linear-gradient(180deg, rgba(255,255,255,0.6), rgba(255,255,255,0.6)),
                url(http://r.dynamis.jp/foxkeh.png) !important;
  }
}
