css:
/* Stylishソース：mixi用 */
/* Stylish source : for mixi */
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document domain("mixi.jp") {
  /* アプリ周辺広告消し */
  /* ページ背景画像に埋め込まれている オレンジ線消し */
  body {
    background: none !important;
  }
 
  /* ページ背景画像に埋め込まれている Mixiロゴ消し */
  div#headerArea {
    background: none !important;
  }
 
  /* Mixi画像リンク消し */
  #headerArea div.headerLogo {
    display:none !important;
  }
 
  /* 上部広告非表示 */
  div.adBanner {
    display:none;
  }
 
  /* スマホ・携帯 ヘルプ ログアウト リンク消し */
  div.utilityNavigation {
    display:none;
  }
 
  /* パディング詰め */
  div#bodyArea {
    padding: 0px !important;
  }
 
  div#headerArea div.globalNavigation {
    margin-bottom: 0px !important;
  }
 
  /* mixiポイント残高表示非表示 */
  div.mixiPointDisplay {
    display: none !important;
  }
 
  /* 右部広告非表示 */
  div.adMain {
    display:none;
  }
 
  /* 下部広告非表示 */
  div#footerAdBanner {
    display:none;
  }
 
  /* 下部アプリ情報表示非表示 */
  div#localInnerFooter {
    display:none;
  }
 
  /* 下部mixiリンク表示非表示 */
  div#footerArea {
    display:none;
  }
}
