css:
@-moz-document domain(cracked.com) {

#articleTitle {
    font-family: Trebuchet MS;
    text-align: center;
}
.bylineArticle, .bylineby {
    display: none !important;
}
.date, #viewCounts {
    font-family: Verdana;
    font-size: 14px;
    font-style: normal !important;
}
#metadata {
    text-align: center;
}
figure {
    text-align: center;
}
#persistent-share {
    display: none !important;
}
.subheading {
    font-family: Trebuchet Ms;
    text-align: center;
}
p {
    padding: 0 9px;
    font-family: Verdana;
}
#SecondaryContent {
    display: none !important;
}

#Blog>#PrimaryContent, #SecondaryContent{
    float: none !important;
}
#Article>#PrimaryContent, #SecondaryContent{
    float: none !important;
}
section.body.FLC {
    text-align: left;
}
#Ad300Main {
    display: none !important;
}
#additional_news {
    margin-left: 10px;
} 
.ad {
    display: none !important;
}
.authorSignature {
    text-align: center;
}
article.ArticleArchive.Module {
    display: inline;
}
#ArticleLanding>#PrimaryContent {
    float: none !important;
}
#SiteHome>#PrimaryContent {
    float: none;
}
#herbie-widget-35, #Ad990 {
    display: none !important;
}
.favorites {
    float: none;
}
.fbSendWrapper { 
    display: none;
}
.Ad300x250 {
    display: none;
}
#specialFeature iframe {
    display: none;
}
p img {
    text-align: center;
}
#inline-share-buttons {
    display: none !important;
}
}
