css:
@-moz-document domain("sict.moodle.aau.dk") {
a:link, a:visited {
    color: #BF4040 !important;
}

a:hover {
    color: #E08585 !important;
}



.generalbox.box p {
    color: #4B4B4B !important;
}

.singlebutton input {
    opacity: 0.6 !important;
    cursor: pointer !important;
}

.singlebutton input:hover {
    opacity: 1 !important;
}








#profileoptions .loginform {
    padding-right: 3px !important;
    margin: 3px !important;
    margin-right: 50px !important;
}

#profileoptions #submit {
    bottom: 53px !important;
    position: relative !important;
    left: 30px !important;
    cursor: pointer !important;
    width: 65px !important;
}

#login li {
    display: block !important;
}

#profileoptions .loginform input {
    width: 150px !important;
}

#languageoptions {
    position: relative !important;
    bottom: 2px !important;
    pointer-events: none;
}

#languageoptions .popupform{
    pointer-events: auto !important;
}







.sideblock div.column.c0 {
    display: none !important;
}

.sideblock .content .list li {
    margin: 2px 10px 16px 10px !important;
}

.sideblock .footer {
    margin-top: 20px !important;
    margin-left: 5px !important;
}

.sideblock .header .hide-show-image {
    opacity: 0.2 !important;
    padding-top: 3px !important;
    margin-top: 4px !important;
}

.sideblock .header .hide-show-image:hover {
    opacity: 0.6 !important;
}

.sideblock .header .title {
    margin-left: 7px !important;
}

.sideblock .content {
    background-color: #FFF !important;
}

.block_calendar_upcoming .event .date {
    margin-top: 5px !important;
    margin-right: 2px !important;
}

.block_calendar_upcoming .event .date br {
    display: none !important;
}






.day.today {
    border-width: 1px !important;
    border-color: #808080 !important;
    padding: 2px !important;
}







.navbar .navbutton form div input {
    opacity: 0.4 !important;
    cursor: pointer !important;
}

.navbar .navbutton form div input:hover {
    opacity: 0.7 !important;
}







.activity.label {
    margin-top: 10px !important;
    margin-bottom: 10px !important;
    margin-left: 20px !important;
    line-height: 1.4em !important;
}

h1 {
    line-height: 1.2em !important;
}

.summary p {
    margin: 0 !important;
}

.summary h1 {
    margin: 0 !important;
    width: 420px !important;
}







.coursebox {
    padding-top: 10px !important;
    padding-bottom: 10px !important;
}

.my .coursebox .overview {
  text-align: center !important;
}

.my .coursebox .overview .info {
    margin-left: auto !important;
    margin-right: auto !important;
}

h1.main, h2.main, h3.main, h4.main, h5.main, h6.main {
    padding-top: 0 !important;
}






#sict_menu ul li ul li a:hover, #sict_menu ul li ul li a:active {
    background: #FFE6E6 !important;
}





.breadcrumb li {
    opacity: 0.7 !important;
}



.right.side {
    opacity: 0.3 !important;
}



.block_elsa.sideblock {
    display:none !important;
}

.block_html.sideblock {
    display:none !important;
}

.block_online_users.sideblock {
    display:none !important;
}

.block_participants.sideblock {
    display:none !important;
}

.block_search_forums.sideblock {
    display:none !important;
}



.block_admin.sideblock {
    border: 0 !important;
    width: auto !important;
    position: absolute !important;
    top: 196px !important;
    margin-left: 644px !important;
    z-index: 1 !important;
}

.block_admin.sideblock .header {
    display: none !important;
}

.block_admin.sideblock .content .list .r1 .column.c1 {
    display: block !important;
}

.block_admin.sideblock .content .list .r1 a {
    display: block !important;
    width: 58px !important;
    height: 12px !important;
    overflow: hidden !important;
    margin: 0 !important;
    border: 1px solid #87939C !important;
    padding: 2px !important;
    font-size: 11px !important;
    background-color: buttonface !important;
    opacity: 0.4 !important;
    color: buttontext !important;
    text-align: center !important;
}

.block_admin.sideblock .content .list .r1 a:hover {
    opacity: 0.7 !important;
}

.block_admin.sideblock .r0 {
    display: none !important;
}
}
