css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {



#home_button.tab.iconic > a {
  width: 9px !important;
  height: 37px !important;
  background-image: url("http://25.media.tumblr.com/tumblr_ma1reyTFJb1qefa3ho1_100.png") !important;
  background-size: 44px 37px !important;
  margin-left: 7px !important;
  margin-top: -9px !important;
  padding-left: 44px !important;
  padding-top: 9px !important;
  opacity: .65 !important;
}
#home_button.tab.iconic > a:hover {
  opacity: .75 !important;
}
#home_button.tab.iconic.selected > a,
#home_button.tab.iconic.selected > a:hover  {
  opacity: 1 !important;
}

/* peasants?   */
#home_button .tab_notice {
    left: 63px !important;
    right: auto !important;
}
 
    
}
