css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iichan.hk") {

html, body {
    background-color:#fdfdfd!important;
    font-family: Segoe UI,Tahoma,Arial,Verdana,sans-serif;
    color: #333 !important;
    padding: 0 !important;
}
body{
    background: url("http://nyapics.ru/images/24.png") no-repeat fixed right bottom, url(data:image/gif;base64,R0lGODlhCAAIAJEAAMzMzP///////wAAACH5BAEHAAIALAAAAAAIAAgAAAINhG4nudroGJBRsYcxKAA7) repeat fixed!important;
    display:inline-block !important;
    width: 100%;
}
form{
    padding: 0 8px 0 8px !important;
}
a{
    color: #26A0DA !important;
}

/*dollfix*/

#de-qarea{
    width:760px !important;
}
#de-qarea.reply{
    background: none !important;
    border:none !important;
    margin: 0 0 0px 0px !important;
    box-shadow: 0 0 0px 0 #ccc !important;
    border-radius: 0px !important;
}
#de-txt-panel{
    float:left !important;
    margin-top:4px;
}
#de-cfg-common textarea{
    width:360px !important;
}
#de-spell-div textarea{
    width:330px !important;
}
#de-panel, #de-cfg-bar, #de-cfg-head, #de-panel{
    background: #6880A6 !important;
}
.de-cfg-tab-back {
    border-color: #6880A6 !important;
}
#de-content-hid, #de-content-fav{
    background: rgba(240,240,240,0.75) !important;
}
.postarea, #de-pform {
    padding: 8px 14px 0 0 !important;
}
.de-img-full{
    border-radius: 3px;
    box-shadow: 0 0 4px 0 rgba(0, 0, 0, 0.3)!important;
    border: 1px solid #ccc !important;
}
/*adminbar*/
.adminbar {
    background: #fff;
    border-bottom: 10px solid rgba(90, 120, 160,0.6);
    box-shadow: 0 0 4px 0 rgba(0, 0, 0, 0.3);
    clear: both;
    padding: 8px 0 !important;
    text-align: center !important;
    width: 100%;
}
.doubledash{
    display:none;
}
.adminbar a {
    color: #333 !important;
    text-decoration: none !important;
}
.adminbar a:hover {
    color: #6681A4 !important;
}
.logo {
    margin-top: 20px;
    clear: both;
    color: #6681A4 !important;
    font-size: 40px !important;
    font-weight: lighter !important;
    text-align: center;
    width: 100%;
    display: inline-block;
}
.logo::-moz-selection {
    background:  rgba(220, 220, 220, 0.7);
}
.banner, .greeting img {
    border-radius: 3px;
    box-shadow: 0 0 4px 0 rgba(0, 0, 0, 0.3);
    margin-top: 10px;
}
hr{
    margin: 8px 10px;
    border: 0;
    height: 1px;
    background: #333;
    background: -webkit-linear-gradient(left, #ccc, #6681A4, #ccc); 
    background:    -moz-linear-gradient(left, #ccc, #6681A4, #ccc); 
    background:     -ms-linear-gradient(left, #ccc, #6681A4, #ccc); 
    background:      -o-linear-gradient(left, #ccc, #6681A4, #ccc); 
}
/*reply_form*/
input:focus{
    outline: none!important;
}
.postarea, #de-pform {
    background: rgba(255, 255, 255, 0.75);
    margin: 15px auto;
    padding: 10px;
    width: 706px;
    box-shadow: 0 0 4px 0 rgba(0, 0, 0, 0.3);
}
table{
    margin-bottom:10px;
}
.postblock {
    background: #a5b9d7 !important;
    color: #333 !important;
    font-weight: lighter !important;
    padding: 2px 15px;
}
input[name="nya2"], textarea{
    width:540px;
}
input[name="nya3"]{
    width:438px;
}
input[name="captcha"]{
    width:218px; 
}
input[name="password"]{
    width:420px;
}
input[type="submit"]{
    -webkit-transition: 0.5s;
    transition: 0.5s;
    background: #eee !important;
    border: 1px solid #ccc;
    font-family: Segoe UI,Tahoma,Arial,Verdana,sans-serif;
    font-size:15px;
    padding: 1px 7px;
    color: #444!important;
    float:right;
}
input[type="submit"]:hover{
    -webkit-transition: 0.5s;
    transition: 0.5s;
    background: #92C9E3 !important;
}
input[type="submit"]:active{
    -webkit-transition: 0.5s;
    transition: 0.5s;
    font-size:15px;
    padding: 1px 7px!important;
    border: 1px solid #ccc!important;
    background: #92C9E3 !important;
    box-shadow: 0 0 10px 0 rgba(0, 0, 0, 0.3) inset;
    font-family: Segoe UI,Tahoma,Arial,Verdana,sans-serif;
}
input[name="nya2"],input[name="nya3"], input[name="captcha"], input[name="password"], textarea{
    font-family: Segoe UI,Tahoma,Arial,Verdana,sans-serif;
    font-size:15px;
    -webkit-transition: 0.5s;
    transition: 0.5s;
    color: #444!important;
    border: 1px solid #ccc;
    padding: 2px 6px;
}
textarea{
    resize:vertical;
    padding: 6px !important;
}
textarea:focus{
    outline: none;
}
input[name="nya2"]:hover,input[name="nya3"]:hover, input[name="captcha"]:hover, input[name="password"]:hover, textarea:hover{
    -webkit-transition: 0.5s;
    transition: 0.5s;
    box-shadow: 0 0 6px 0 rgba(0, 0, 0, 0.2);
    outline: 0px none;
}
input[name="nya2"]:focus,input[name="nya3"]:focus, input[name="captcha"]:focus, input[name="password"]:focus, textarea:focus{
    -webkit-transition: 0.5s;
    transition: 0.5s;
    box-shadow: 0 0 6px 0 #7da1d8, 0 0 6px 0 rgba(0, 0, 0, 0.2) inset;
}
.rules{
    background: #fafafa;
    border:1px solid #ccc
}
.rules a{
    color: #26A0DA !important;
    text-decoration: none !important;
}
/*threads*/
.filesize a{
    color: #26A0DA !important;
    text-decoration: none !important;
}
.thumbnailmsg{
    color: #333!important;
}
.thumb{
    border-radius: 3px;
    box-shadow: 0 0 2px 0 #666666;
}
.filetitle{
    color: #26A0DA !important;
}
.postername, .commentpostername{
    color: #6983AA !important;
}
.reflink a{
    color: #6c8e92 !important;
}
.spoiler{
    background: #ddd !important;
    color: #ddd !important;
}
.spoiler:hover{
    background: #ddd !important;
    color: #333 !important;
}
.reply, .userdelete{
    background: rgba(240,240,240,0.9) !important;
    border:1px solid #ccc;
    margin-bottom:20px !important;
    color:#333 !important;
    box-shadow: 0 0 4px 0 #ccc;
    border-radius: 4px;
}
.replytitle{
    color:#666 !important;
}
.reply:hover{
    box-shadow: 0 0 4px 0 rgba(105,131,170,0.5), 0 0 3px 0 rgba(105,131,170,0.5) inset;
}
.theader{
    background: none !important;
    width:668px !important;
    color: #333 !important;
    font-size:18px;
    margin: 10px auto -15px;
    line-height: 30px;
    height: 0;
    border-right: 25px solid transparent;
    border-left: 25px solid transparent;
    border-bottom: 30px solid rgba(105, 131, 170,0.6);
}
.highlight{
    background:  rgba(200, 215, 239,0.6) !important;
    color: #333 !important;
    border: 2px dashed rgba(0, 0, 0, 0.3) !important;
}
/*footer*/
table, td{
    border: none !important;
}
.userdelete input[name="password"]{
    width:120px;
}
.footer{
    display:none
}
/*main*/
.greeting img{
    display: block;
    margin: 0 auto;
}
.greeting p{
    font-size: 18px !important;
    display: inline-block !important;
    background:  rgba(240, 240, 240,0.6) !important;
}
.greeting p, .news{

    color: #6681A4 !important;
    font-weight: lighter !important;
    box-shadow: 0 0 4px 0 rgba(105,131,170,0.5), 0 0 3px 0 rgba(105,131,170,0.5) inset;
    margin-top:10px !important;
    padding:4px 10px;
}
.news{
    background:  rgba(255, 255, 255,0.6) !important;
    font-size: 24px !important;
}
.sublogo{
    margin:10px auto !important;
    width:160px !important;
    background:  #fafafa;
    color: #333 !important;
}
.logo a{
    text-decoration: none !important;
}
.logo a::after{
    right:16px;
    padding:2px 16px 2px 62px;
    content: "chan"; position: absolute !important;
    background: url("http://nyapics.ru/images/27.png") no-repeat scroll 24% 40% #fafafa;
}
.logo a::after, .sublogo{
    border-radius: 3px !important;
    box-shadow: 0 0 4px 0 rgba(105,131,170,0.5), 0 0 3px 0 rgba(105,131,170,0.5) inset;
}
.category{
    margin:0 !important;
}
.list{
    background: #f9f9f9 !important;
}
.list a{
    -webkit-transition: 0.5s;
    transition: 0.5s;
    padding-left:10px!important;
}
.list a:hover{
    -webkit-transition: 0.5s;
    transition: 0.5s;
    padding-left:20px!important;
    background: #ddd !important;
}
.header{
    background: #9CAEC6 !important;
    color: #fefefe !important;
    font-weight: lighter !important;
    font-size: 15px !important;
    padding:1px 2px !important;
}
}
