css:
@-moz-document domain(tumblr.com) {
body {background: url('http://static.tumblr.com/fjjcjls/ThIma1e2g/3.png') center right #bebebe repeat fixed !important;} 
*{text-shadow:none !important; color:#7b7b7b !important;} 
 
.post_question {background: #e4f1db !important;}
.post_title {font-family: calibri !important; font-size: 22px !important; text-shadow:2px 0px 1px #e3e3e3 !important;}

#inbox_button {background: #ced7b7  !important; border-radius: 5px !important; margin: 2px !important; }

#help_button {background: #ced7b7  !important; border-radius: 5px !important; margin: 2px !important; }

#preferences_button {background: #ced7b7  !important; border-radius: 5px !important; margin: 2px !important; }

#logo {background-image: url('http://static.tumblr.com/fjjcjls/W9xma1fmk/tumblr.png')   !important;  margin-top: 10px !important; }


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#f9f9f9 !important;} 
#nav .nav_item.active 
	{background-color:#f9f9f9} 
.permalink  
	{background: none !important; 
	width: 16px !important; 
	height: 16px !important; 
  	background: #f9f9f9 !important; 
  	border-bottom-left-radius: 10px !important; 
  	border-top-right-radius: 10px !important; 
  	margin-top: 3px !important; 
  	margin-right: 4px !important;} 
h1,h2,h3,h4,h5,h6 
	{color:#9fc189 !important;} 
a, a:visited 
	{color:#9fc189 !important;} 
a:hover 
	{color:#74b647 !important; text-decoration: none !important;} 
 
 
 
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"])  
	{background: none !important; 
	width: 0px !important; 
	height: 0px !important; 
	margin-top: 0px !important; 
	border-bottom: solid 11px white !important; 
	border-right: 11px solid transparent !important; 
	border-left: 11px solid transparent !important; } 
.selection_nipple:not([class="selection_nipple white"])  
	{border-bottom: solid 11px #f9f9f9 !important;}
.notification, .avatar_frame, .nipple{border:0px !important}
 
 
 
#dashboard_switch_blog_menu_current, 
#dashboard_switch_blog_arrow_and_divider, 
#dashboard_switch_blog_arrow_current 
	{background-image:none !important;} 
#new_post_notice_container 
	{color:#bebebe !important;} 
#new_post_notice_container a 
	{color:#bebebe !important;} 
ol#posts li.notification 
	{background-color: #f9f9f9 !important; 
	border-bottom: 2px solid #9d92a5 !important; 
	color: #000000 !important;} 
 
 
 
.notification_type_icon{display:none !important} 
.selected .following, 
.selected .followers, 
.selected .settings, 
.selected .posts, 
.selected .messages, 
.selected .drafts, 
.selected .queue,  
.selected .likes  
	{color: #A099A1 !important; 
	background: #e9e9e9 !important;} 
 
 
 
a.explore_more_tags, 
a.add_and_remove, 
a.customize, 
a.mass_editor 
	{background-color:#e9e9e9 !important;   
	background-image:none !important; 
	border:0px !important;} 
div.gradient, 
div.dashboard_nav_item,  
a.permalink 
	{background-color:#e9e9e9 !important; 
	opacity:.9;  
	background-image:none !important; 
	border:0px !important;} 
.content.is_media,  
.mask.top,  
.mask.bottom  
	{background: none !important; } 
.post.photo_post .photo,  
.post.photo_post .video_thumbnail  
	{border-radius: 16px !important;} 
.post.photo_post .photo,  
.post.photo_post .video_thumbnail  
	{-moz-box-shadow: 1px 1px 3px #bebebe !important;} 
 
 
 
#auto_pagination_loader 
	{background: #bebebe !important;} 
div.return_to_top_icon{	background: none !important; 
	width: 0px !important; 
	height: 0px !important; 
	margin-top: 0px !important; 
	border-bottom: solid 30px white !important; 
	border-right: 15px solid #bebebe !important; 
	border-left: 15px solid #bebebe !important;} 
 
 
 
 
#left_column #posts .post .permalink 
	{background-color:#f9f9f9;} 
#left_column #posts .notification  
	{background-color:#f9f9f9 !important; 
	color:#B85858 !important; 
	border-bottom-color: #eee !important;} 
#left_column #posts .post .post_avatar 
	{-moz-border-radius: 1px !important;  
	-webkit-border-radius: 2px !important;  
	width: 64px !important; height: 64px !important;  
	margin: 0px !important;} 
#left_column #posts .post .sub_avatar_inner 
	{-moz-border-radius: 8px !important;  
	-webkit-border-radius: 10px !important;  
	width: 32px !important; height: 32px !important;  
	margin: 0px !important;} 
 
 
 
 
.new_post_label_icon{background-image:none !important;}
/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
#new_post{background: url('http://i798.photobucket.com/albums/yy266/nautiical/tumblr/e03d284f.png') center no-repeat !important; background-color:none !important;}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:0px;
    opacity: 0;
    display: block;}
.new_post_label{
    font-size:0px !important;
    padding-top:0px !important;
    opacity: 0;}
}
