css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {
#gallery-admaster * {background:#ffcccc !important;}

#ad-blocking-makes-fella-sad,
#comments .alink,
.column2 .read>.subsection:first-child,
.rr>.box+.box,
#news-side-top .section+br+.section,
#forum>.catbar+div,
.ads,
#adso-magnifico,
#output>.ie-paintfix, #gallery-admaster,
.sleekadbubble,
#moreAdFrame,
.hidoframe,
.hh>div[style~="overflow:"],
.ad-blocking-makes-fella-confused {
display:none !important;
position:absolute !important;
left:-9999px !important;
top:-9999px !important;
}
.stream-ads {
border:0 !important;
}
#forum>.comments {
padding-right:0 !important;
min-height:0 !important;
}
#comments>.c {
background:#92A399 !important;
}

#world.withad #midSection {
top:42px !important;
}
#world.withad #headerTop {
height:115px !important;
}
body.withad #appFolder {top:80px !important;}
body.withad #appFolderShadow {top:75px !important;}
#world.withad #headerTop i.tl, #world.withad #headerTop i.tr {top:116px !important;}
}
