/* blogger.com - Mandriva blue                                                      */
/* by Agnelo de la Crotche - 05.14.2008  - http://www.unixversal.com                */
/* This is a dark blue colored blogger.com style                                    */
/*                                                                                  */
/* other styles based on the same rules set:                                        */
/* blogger.com - Ubuntu Coffee beans    http://userstyles.org/styles/7201           */
/* blogger.com - Fedora slate blue      http://userstyles.org/styles/7348           */
/*                                                                                  */
/* other Mandriva blue styles:                                                      */
/* wikipedia.org - Mandriva blue        http://userstyles.org/styles/6083           */
/*                                                                                  */
/* Wikipedia styles:                                                                */
/* wikipedia.org - Black & silver       http://userstyles.org/styles/6044           */
/* wikipedia.org - Ubuntu coffee beans  http://userstyles.org/styles/6069           */
/* wikipedia.org - chameleon green      http://userstyles.org/styles/6080           */
/* wikipedia.org - Mandriva blue        http://userstyles.org/styles/6083           */
/* wikipedia.org - Mepis blue           http://userstyles.org/styles/6110           */
/* wikipedia.org - Fedora slate blue    http://userstyles.org/styles/6157           */
/* wikipedia.org - Ubuntu Gold          http://userstyles.org/styles/6464           */
/*                                                                                  */
/* LCARS redesigned styles :                                                        */
/* Black Google in LCARS terminal               http://userstyles.org/styles/6738   */
/* Black Google in LCARS - supplemental         http://userstyles.org/styles/6906   */
/* wikipedia.or Start Trek LCARS terminal       http://userstyles.org/styles/6594   */
/* userstyles.org Star Trek LCARS redesigned    http://userstyles.org/styles/6510   */
/* Enjoy !                                                                          */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

@-moz-document domain(blogger.com),
               domain(blogspot.com){
html, html#header, body{background: #9ab2ef url("data:image/png;base64,") top left repeat-x !important;} 
* {border-color: #183184 !important}
:focus{outline: none !important}

/* ~~ hide all widgets except mini chat ~~~~~~~~~~~~~~~~~~ */
/* Comment out the following lines if you want to see blogger's widgets (slower!) */
.sidebar .HTML .widget-content>div:not([id="cboxdiv"]){display: none !important}
.sidebar .HTML .widget-content object{display: none !important}
.sidebar .HTML span, .sidebar .HTML h2{display: none !important}
.sidebar .Poll, .sidebar .Image, .sidebar .Text, .sidebar .VideoBar{display: none !important}

/* ~~ hide butons background ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
.ubtn-small * {background-color: transparent !important; background-image: none !important}

/* blogger.com navigation bar ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
#b-navbar{background-color: #0e216c !important; color: #fde20b !important; border-bottom: 3px !important}
.Navbar, #b-search{background-color: #0e216c !important; background-image: none !important}
.btn, .btn div {border-color: #65a5e7 !important; background-color: #2b51a7 !important; color: #000000 !important}
.btn:hover, .btn div:hover {background-color: #fcfe10 !important}
#b-query{border-color: #44517d !important}
#b-query{background-color: #9da7ca !important}
#b-navbar a:link{color: #fde20b !important; font-weight: bold !important}
#b-navbar a:visited{color: #fde20b !important; font-weight: bold !important}
#b-navbar a:hover{color: #ffffff !important; font-weight: bold !important}

/* Flag tooltip */
#flagi, #flagi *{background-color: #67a7e9 !important}

#navbar-logo{background-image: url("data:image/png;base64,") !important}

/* Main page ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */

#wrap, #wrap1, #wrap2, #wrap3, #wrap4, #outer-wrapper, #wrapper,
#main, #main2, #main3, #main4, #main-wrapper, #main-wrap1, #main-wrap2, #main-top,
.post *, .post-footer, .post-body, .post-title, #content-wrapper, h2.date-header, 
#header-wrapper, #header, #blog-header, #header h1, #navigation, #squeeze,
.right-corner, .right-corner *, .left-corner, .left-corner *, #h3, #m2,
body{background: transparent none !important; border: none !important}
#blog-title, #description, #m3{border-color: #fde20b !important}
#header, #header-wrapper{border: none !important}

h1{color: #fde20b !important}
h2{color: #23469e !important; padding-bottom: 10px !important}
h3{color: #000000 !important}
h2.title{color : #ffffff !important}
h2.date-header{color: #2a3550 !important}
.post, .post-body{ border-color: transparent !important}
.description{color: #ffffff !important}

.post-author, .vcard, .post-footer, .post-footer * {color: #1a368c !important}
.post-timestamp{color: #1a368c !important; text-transform: lowercase !important}
.post{border-bottom: 1px solid #4677ca ! important}
.post img:not([class="icon-action"]){border: 1px solid #4677ca ! important; background-color: #5b7eb5 !important}
.post span, .post div{color: #000000 !important}

a.timestamp-link, a.comment-link{color: #1a368c !important}
a.timestamp-link:hover, a.comment-link:hover{color: #0e216d !important}

/* footer  ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
.wrap-image, .wrap2-image, #footer-wrapper, #footer, #footer-wrap1, #footer-wrap2, #footer *, #main-bot {background-image: none !important; background-color: transparent !important}
#footer, .feed-links{color: #152e7e !important}
#footer p span{color: #4d5b8e !important}
/* links */
a{color: #12297c !important; background-color: transparent !important}
a:link{color: #2d55a9 !important; font-weight: bold !important}
a:visited{color: #2d55a9 !important; font-weight: bold !important}
a:hover{color: #fde20b !important; font-weight: bold !important}

/* sidebar ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#sidebar-wrapper, #sidebar, .sidebar *, #profile-container, #profile-container *,
.box, .box1, .box2, .box3, .box4, .sidebar-title, #sidebar li
{background: transparent none !important; border: none !important}
img.profile-img{border: 1px solid #142b7b ! important; background-color: #2f448e !important}
.profile-data{color: #24489e !important}
.profile-textblock{color: #24489e !important}
.sidebar .widget, .main .widget {border-bottom:1px dotted #5373bf !important}
.sidebar input[type="text"]{border: 1px solid #47567d !important; background-color: #82a1f2 !important}
#sidebar-wrap div {background: #6384d9 none !important}
#sidebar-wrap>div, #sidebar-wrap>div>div  {-moz-border-radius: 20px !important; color: #000000 !important}
.widget-content img { background-color: #000000 !important}
.widget-content * { color: #000000 !important}

/* page not found & startpage ~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
.arrow-large-right .ubtn-orange-on-white div.ubtn,
.arrow-large-right .ubtn-orange-on-white span.ubtn span,
.arrow-large-right .ubtn-orange-on-white div.ubtn .i,
.arrow-large-right .ubtn-orange-on-white span.ubtn a{background-color: transparent !important;
background-image: url("data:image/png;base64,")!important; margin-left: -6px !important}

/* Chat Box  ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#cboxmain, #cboxform {border-color: #3054a8 !important}

/* Icon replacements ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
img[src$="icon18_email.gif"]{height: 0px !important; width: 18px !important; padding-top: 18px !important;
background: url("data:image/gif;base64,R0lGODlhEgANAMIIAAABAAwbQicpJjRFcFl3xGOE2f2cAdyuZywAAAAAEgANAAADXhi63F0wSrlImLgEogYJURgFxKAMAxGIEBAQqIIORMAawKzMAxFAAAHAYACgFLwAIVAAHA5EAGCgmAUIqABAACACUApUgMCbEQMoxSBAKM8AA0JgsCAM7vj8neDo9xMAOw==") no-repeat !important}

img[src$="openid-inputicon.gif"]{height: 0px !important; width: 16px !important; padding-top: 16px !important;
background: url("data:image/gif;base64,R0lGODlhEAAQAIQQAP9jAP9pEft4Kv6ygNLBusjLyODOw9HU0tzTzNze29zi5P/k1fLv6u3y9f727vz++////////////////////////////////////////////////////////////////yH+EUNyZWF0ZWQgd2l0aCBHSU1QACH5BAEKABAALAAAAAAQABAAAAVGICSOZGmeaKoug0oGgDsGgawYQIAo6FEUhBzhdyj9FA0HzQFR/AoiH+PxUNaihyLER63SUM7D1JpKEBGAmCpxIAhasng8BAA7") no-repeat !important}

img[id="lj-icon"]{height: 0px !important; width: 16px !important; padding-top: 16px !important;
background: url("data:image/png;base64,") no-repeat !important}

img[id="wp-icon"]{height: 0px !important; width: 16px !important; padding-top: 16px !important;
background: url("data:image/png;base64,") no-repeat !important}

img[id="aol-icon"]{height: 0px !important; width: 16px !important; padding-top: 16px !important;
background: url("data:image/png;base64,") no-repeat !important}

img[src$="/icon18_editdoc.gif"]{height: 0px !important; width: 16px !important; padding-top: 16px !important;
background: url("data:image/gif;base64,") no-repeat !important}

img[src$="plus_google.gif"]{height: 0px !important; width: 62px !important; padding-top: 18px !important;
background: transparent url("data:image/gif;base64,") no-repeat !important}
}

/* start page */
@-moz-document url-prefix(https://www.blogger.com/start){

/* header  ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#header *{background: transparent !important}
#header label{color: #fcfe10 !important}
#h2{border-bottom: 4px solid #2f58ac !important}
#h3{border-bottom: 0px !important}

#tag em{color: #a0b3e1 !important}
#start-login-form input.text{border-color:#000000 #2d56a9 #2d56a9 #000000 !important; background-color: #92a1d5 !important}
#header #language{border-color:#000000 #005aff #005aff #000000 !important; background-color: #5474c3 !important; color: #000000 !important}
#header option, #header option[selected]{background-color: #2a50a5 !important; color: #000000 !important}
input[type="submit"]{border-color: #5993d9 #1f3f96 #1f3f96 #5993d9 !important; color: #698cdf !important}

/* body  ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#main, #m2, #home, #primary{background: transparent none !important; color: #000000 !important}
#m3{border: none !important; color: #ffffff !important}
#main{border: 3px solid #1d3c93 !important; -moz-border-radius: 20px !important; background-color: #2e56aa !important}
h1{color: #000000 !important}
#primary p{color: #12297c !important}
#secondary em{color: #ffd509 !important}

a{color: #943f06 !important}
a:hover{color: #ffffff !important}

/* Take a quick tour - button */
#home #primary #tour-btn, #home #primary #tour-btn div{background-image: none !important}
#home #primary #tour-btn{border : 3px solid #7791d7 !important; -moz-border-radius: 20px !important; background-color: #132b7f !important}
#home #primary #tour-btn:hover {background-color: #0a1c5c !important}
#home #primary #tour-btn div{background-color: transparent !important}
#home #primary #tour-btn div a span{color: #ffffff !important}

/* left and right arrows */
img[src$="img/arrow-right.gif"]{height: 0px !important; width: 14px !important; padding-top: 14px !important;
background: url("data:image/gif;base64,R0lGODlhDgAOAMIFAAUzYwU1ZwU2aTdfiWyw79jqPdjqPdjqPSH5BAEKAAcALAAAAAAOAA4AAANGeKoDMCse4SoQ0hFi3RLOtlmCUgkiMVTH0KXEAAxBBwwpIAg2IIgAwaAjSA0Ag0NFkBpUFAJHyiJYODY2icAmkCwGjoEkAQA7") no-repeat !important}

img[src$="img/arrow-left.png"]{height: 0px !important; width: 14px !important; padding-top: 14px !important;
background: url("data:image/gif;base64,R0lGODlhDgAOAMIGAAU0ZQY1aAY3bAw/dFCQzWyw79jqPdjqPSH5BAEKAAcALAAAAAAOAA4AAANIeKoCICue4CoI0rpS3AoWwRVOoFTDWATVIQCDWlSOEADqQFcBMAY7R0AAGKgCO8GhMlAFKooAjTByBBZBjkMSCAYkC4FDIEkAADs=") no-repeat !important}

/* step 1,2,3 */
#step1{background-image: url("data:image/png;base64,") !important} 

#step2{background-image: url("data:image/png;base64,") !important}

#step3{background-image: url("data:image/png;base64,") !important} 


/* Publish - Get - Post - Go */
#w-pub{background: #febb05 url("data:image/png;base64,") no-repeat scroll center top !important}

#w-con{background: #febb05 url("data:image/png;base64,") no-repeat scroll center top !important}

#w-shr{background: #febb05 url("data:image/png;base64,") no-repeat scroll center top !important}

#w-pst{background: #febb05 url("data:image/png;base64,") no-repeat scroll center top !important}

#w-pub:hover, #w-con:hover, #w-shr:hover, #w-pst:hover{background-color: #0f2370 !important}
#home #primary td a, #home #primary td a strong{color: #ffffff !important}
}

/* leave a comment page ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
@-moz-document url-prefix(https://www.blogger.com/comment){

/* comments */
body{margin-bottom: 25px !important; color: #000000 !important}
#header{display: none !important}
h1{color: #fdd509 !important;}
h2{text-align: left !important; padding-left: 0px !important; color: #98a5d7 !important}
h4.total{color: #bcc5ea !important}
#wrap{width: 740px !important}
#wrap3{width: 100% !important; border: none !important}
#sidebar{width: 350px !important} 
#cbody{color: #000000 !important}
#wrap, #wrap2, #wrap3, #cform {background-image: none !important; background-color: transparent !important}
#wrap{background-color: #5576cc !important; border: solid 4px #1f3f96 !important;-moz-border-radius: 1em !important}
#main, #sidebar{background: #5576cc none !important}
#btnAll{color: #95a5e5 !important}
#btnAll:hover{color: #ffffff !important}
.msg, .saved{background-color: transparent !important; color: #ffffff !important}

img.profile{border: 1px solid #193285 ! important; background-color: #294da5 !important}
.comment-timestamp{color: #163187 !important}
#comments-block dd{border-bottom: 1px solid #677dc5 !important; color: #000000 !important}
#comments-block dd p {color: #000000 !important}
#comments-block dt, #comments-block dt span{color: #2d408b !important}
#comments-block dt#preview-header, #comments-block dd#preview-main {background-color: #7086c3 !important}

/* sidebar */
#sidebar, #cbody{border:none !important}
#cform{background-color: #294a9e !important; border: solid 2px #6782c9 !important; -moz-border-radius: 20px !important}
#cform .form-title{background-image: none !important}
h2.sidebar-title{color: #f4cd12 !important}
textarea#comment-body{background-color: #82a1f2 !important; height: 250px !important}
#sidebar2 #cbody #buttons {border: none !important}
input.text, input[type="text"], select[id="openIdDropDown"]{border-color:#000000 #47567d #47567d #000000 !important; background-color: #82a1f2 !important}
h3 label{color: #95a2c5 !important}

#publishBtn .i, #publishBtn-hidden, #previewBtn .i, #previewhBtn-hidden
{background-color: #132b7e !important; border: solid 1px #6081d6 !important; -moz-border-radius: 20px !important}
#publishBtn .i:hover, #publishBtn-hidden:hover, #previewBtn .i:hover, #previewhBtn-hidden:hover{background-color: #0f226e !important}

/* links */
#wrap3 a:link{color: #0f236f !important; font-weight: bold !important}
#wrap3 a:visited{color: #0f236f !important; font-weight: bold !important}
#wrap3 a:hover{color: #ffffff !important; font-weight: bold !important}
#bloggerloginform a:link{color: #12297c !important; font-weight: bold !important}
#bloggerloginform a:hover{color: #ffffff !important; font-weight: bold !important}
a[onclick], a[onclick] *{color: #feb304 !important}
a[onclick]:hover, a[onclick] *:hover{color: #ffffff !important; text-decoration: none !important}
#preview-actions a[onclick], #preview-actions a[onclick] *{color: #0f236f !important; text-decoration: none !important; font-size: 90% !important}
#preview-actions a[onclick]:hover, #preview-actions a[onclick] *:hover{color: #fbe304 !important}
}

/* content warning ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
@-moz-document url-prefix(https://www.blogger.com/blogin){
#header{display: none !important;}
#main{margin-top: 150px !important;}
#main, #m2, #m3, .mod{background: transparent none !important; border: none !important}
.mod, #m2{border: solid 2px  #a35623 !important;-moz-border-radius: 20px !important; background-color: #6f0c0c !important}
.mainClm *{color : #ffffff !important;}
.mainClm h1{color : #ff0000 !important; padding: 5px !important; text-align: center !important;}
.mod a, #m3 a, .mainClm p a{color: #fcf30e !important;}
.mod a:hover, #m3 a:hover,.mainClm p a:hover{color: #ffffff !important;}
#continueButton, #exitButton{background-color: #feb304 !important; border: solid 1px #6081d6 !important; -moz-border-radius: 20px !important}
#continueButton:hover, #exitButton:hover{background-color: #fde20b !important}
a[onclick], a[onclick] *{color: #000000 !important}
a[onclick]:hover, a[onclick] *:hover{color: #000000 !important; text-decoration: none !important}
}

/* profile page ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
@-moz-document url-prefix(http://www.blogger.com/profile){
#header{display: none !important;}
#main-wrap *, #sidebar *, #footer *{background-color: transparent !important; border-color: #546bb4 !important;background-image: none !important;}
#main, #m3, .mod3{background: transparent none !important; border: none !important}
#m2, #sidebar .mod3{border: solid 2px  #546bb4 !important;-moz-border-radius: 20px !important; background-color: #7685c0 !important; color: #ffffff !important}
#sidebar .mod3 {background-color: #3762b4 !important; color: #000000 !important}
#m3 h1 { color: #fcf30e !important;}
#sidebar .mod3 h2{color: #ffffff !important}
#sidebar .mod3 th{color: #bac1cd !important}
#sidebar .mod3 td{color: #cbdbea !important}
#sidebar .mod3 a{color: #fcf30e !important}
#sidebar .mod3 a:hover{color: #ffffff !important}
}

/* mini chat ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
@-moz-document domain(cbox.ws){
body{background-color: #23479f !important}
#bdy{background-color: #1d3b92 !important}
#mt{background-color: #95a5e5 !important}
.stxt, .stxt *{background-color: #9aadda !important; color: #000000 !important}
.stxt2, .stxt2 *{background-color: #829bd9 !important; color: #000000 !important}
.dtxt, .dtxt2 {color: #23469e !important}
input{background-color: #9dafdf !important}
input.frmbtn{background-color: #183386 !important; color: #ffffff !important}
a:link{color: #fcf30e !important; font-weight: bold !important}
a:visited{color: #fcf30e !important; font-weight: bold !important}
a:hover{color: #ffffff !important; font-weight: bold !important}
}

/* Google account page (joke !) ~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
@-moz-document url-prefix(https://www.google.com/accounts),
               url-prefix(https://www.blogger.com/comment){
img[src$="google_small_accounts.gif"]{height: 44px !important; width: 0px !important; padding-left: 113px !important;
background: url("data:image/gif;base64,") no-repeat !important}

img[src^="https://www.google.com/accounts/Captcha"],img[src^="/captcha"]
{height: 70px !important; width: 0px !important; padding-left: 200px !important;
background: url("data:image/jpg;base64,") no-repeat !important}
}
