css:
@-moz-document url-prefix("https://www.tumblr.com") {
/* main background and bg color */
body, #footer, div#footer_container.clearfix, #auto_pagination_loader, #search_actions_search, #search_actions_search .posts_wrapper_inner.clearfix, #search_actions_search .search_results_top_wrapper.blogs_wrapper.clearfix, #search_actions_search .search_flex_loader, #search_actions_search .search_results_bottom_wrapper, #search_actions_search .blogs_wrapper_carousel, .l-container.l-container--flex .l-content {
    background: #000000 url(http://i.imgur.com/jbRx1WC.jpg) left fixed !important;
    background-size: cover !important;
    }
/* new header bar */
.l-header-container {
    background: rgba(0,0,0,0.7) !important;
    }
/* fix hover color on sidebar controls and recommended blogs */
.controls_section_item:hover, .recommended_tumblelogs li:hover, .discover .controls_section.follow_list .item:hover {
    background-color: rgba(0,0,0,1) !important;
    }   
#auto_pagination_loader {
    border: none !important;
    }
/* search tweaks */
.search_query {
    background: rgb(20,20,20) !important;
    }
#search_actions_search .search_query, #tagged_actions_display .search_query {    
    color: #ffffff !important;
    }
#search_actions_search .tumblelog_mask_item {
    background: none !important;
    }
.search_query {
    color: #ffffff !important;
    }
/* increase visibility of tumblr and web search buttons in search form (depricated) .search_query_input_controls .search_query_btn {background: #ffffff !important;} */
/* #search_actions_search .search_query_input_controls .search_query_btn {background: rgb(186, 186, 186) !important;} */
#search_actions_search .blogs_wrapper_carousel:after, #search_actions_search .blogs_wrapper_carousel:before {   
    background: none !important;
    }
.discover-controls-wrapper {
    background: rgb(20,20,20) !important;
    }
/* in-dash notifications */
.notification_inner {
    background: rgba(20,20,20,0.6) !important;
    }
/* black content fill and gradient at the top of page */
#dashboard_index .l-content, #dashboard_posts_editing .l-content, #dashboard_useraction_followers .l-content, #activity_actions_index .l-content, #dashboard_drafts .l-content, #dashboard_group_members .l-content, #dashboard_post_queue .l-content, #dashboard_posts_likes .l-content, #dashboard_messages_inbox .l-content, #dashboard_messages_submissions .l-content, #tagged_actions_display .l-content {
    background: url(data:image/png;base64,) top repeat-x !important;
    padding-top: 20px !important;
    }
/* selection nipple deprecated .selection_nipple {border-bottom: 12px solid rgb(20,20,60) !important;} */
/* fix post submission forms and customize appearance forms */
.mceContentBody, #upload_image_form {
    background: #ffffff  !important;
    } 
.dashboard_iframe {
    background: transparent !important;
    }
/* follow button in notifications */
.control.follow.ui_follow {
    background: rgba(255,255,255,0.8) !important;
    }
/* "dog ear" permalink fix */
#dashboard_index .post_permalink, #dashboard_drafts .post_permalink, #dashboard_post_queue .post_permalink, #search_actions_search .post_permalink, #dashboard_posts_likes .post_permalink { 
    background: rgb(60,60,60) !important;
    border-color: #000010 #000010 transparent transparent !important;
}
/* remove blue background from left and right columns, remove errant gradient from the sidebar */
div#right_column,div#right_column:after, .left_column {
    background: none !important;
    }
/* Prev and Next page buttons */
#pagination a {
    color: #eaeaff;
    background: rgba(60,60,60,.3) !important;
    border: 1px solid #eaeaff;
    }
/* transparent icons in queue, add glow to avatars */
.post_avatar {
    background-color: transparent !important;
    border-color: transparent !important;
    }
.post_avatar_link {
    box-shadow: 0px 0px 8px #ffde00;
    }
/* remove gradients from recommended blogs, discovery recommended, and radar section */
.controls_section.user_list li a:before, .radar_footer .radar_avatar:before {
    background: transparent !important;
    border: none !important;
    }
.discover .blog_row .info:before, .blogs-holder {
    background: none !important; 
    border-right: none !important;
    }
/* remove circular background from top-of-page elevator icon */
#discover_actions_discover .elevator, #search_actions_search .elevator {
    background-color: transparent !important;
    }
/* Use a standard white color for follow icons in recommended blogs section */
.icon_plus {
    color: rgba(255,255,255,0.32) !important;
    }
.icon_plus:hover {
    color: rgb(255, 255, 255) !important;
    }
/* transparent post box */    
#posts .post {
    background: rgba(60,60,60,.3) !important;
    transition: all 0.3s linear 0s;
    }
#new_post:hover, #posts .post:hover {
    background: rgba(20,20,20,1) !important;
    transition: all 0.3s linear 0s;
    }
.post-form--form, .post-form--header, .post-form--footer, .post-form--controls {
    background: #fff !important;
    }
/* majority of foreground text */
.post .post_content {
    background: rgba(0,0,0,0) !important;
    color: #eaeaff !important;
    }
.icon_post_text, .new_post a, .post .post_content a, .post_full.is_quote .post_title, .post_source_name_prefix, .search_actions_search .quote, #discover_actions_discover .post_brick.is_quote .post_title, h2 {
    color: #eaeaff !important;   
    }
/* link text */
.post_info_link, .note_link_current, .post_full .post_info, .posts .post_tag, .post_brick .post_tag {
    color: #79A0C4 !important;
    }
/* undo new link underline code */
.post_content a {
    background-image: none !important;
    text-decoration: underline !important;    
    }
.link_text a {
    text-decoration: none !important;
    }
/* featured tags */
.post_tag.featured {
    color: #eaeaff !important; 
    }
/* post nipple beside avatar (depricated) div.post_avatar:after {border-right: 9px solid rgba(60,60,100,.35) !important;} */
/* opacity for the reblog/favorite/other buttons */
.post_control:after { 
    opacity: .6 !important;
    }
.post_control.active:after, .post_control:hover:after {
    opacity: 1 !important;
    }
.post_control.share {
    color: #000000 !important;
    }
/* graident on footer for greater visiblity on post controls */
.post_footer {
    background: linear-gradient(90deg, rgba(60,60,60,0),rgba(60,60,60,0.5),rgba(60,60,60,1));
    } 
/* maintain original color for the activity page and other items */
.notifications_page a, .indash_blog .post_body, .indash_blog .post_body a, .indash_blog .post_title, .indash_blog a, .audio_search_results li a, .audio_tabs li a, .popover_menu_item a, .indash_blog .answer.post_info, .indash_blog .post_notes_label a, .indash_blog .post_full.is_quote .post_title, .indash_blog h2, h2.heading.arrow {
    color: #444 !important;
    }
.indash_blog .post_notes_label a {
    color: inherit !important; 
    }
.indash_blog .description a {
    color: inherit !important; 
    text-decoration: underline; 
    }
.indash_blog .post_tag {
    color: #A1A1A1 !important;
    }
.indash_header_wrapper .navigation a, .indash_header_wrapper .navigation .open_blog_button, .indash_header_wrapper .navigation .info_popover_button { 
    color: #fff !important; 
    }
.indash_blog .post_footer {
    background: inherit;
    }
/* maintain white bg in the settings page */
#settings_actions_tumblelog .settings_group_wrapper, #settings_actions_account #left_column, #settings_actions_dashboard #left_column, #settings_actions_notifications #left_column, #settings_actions_apps #left_column  {
    background: #fff !important;
    }
/* reblogged-from item in posts */
.reblog_icon {
    background: url(data:image/png;base64,) center no-repeat !important; 
    opacity: .75 !important;
    }
/* inbox color corrections */
.note_item {
    color: #444 !important;
    }
.note_item a.name, .note_item .text a, .post_question_fan_mail {
    color: #444 !important;
    }
.answer.post_info {
    color: #eaeaff !important;
    }
.post_content blockquote {
    border-left: solid 3px #eaeaff !important;
    }
.indash_blog blockquote {
    border-left: solid 3px #eee !important;
    }
/* remove ending gradient in the tags field */
.post_tags:after {
    background:none !important;
    }
/* chat post correction */
.post_full.is_conversation .conversation_lines .chat_line:nth-child(2n), .post_brick.is_conversation .conversation_lines .chat_line:nth-child(2n) {
    background: #000020 !important;
    }   
pre {
    background: #000020 !important;
    color: #eaeaff !important;
    }
.indash_blog pre {
    background: none !important;
    color: #444 !important;
    }
/* restore white bg for editing posts */    
#posts .post.editing {
    background: #fff !important;
    }
/* alter color on the link button for specific pages */
#dashboard_index .link_button, #dashboard_drafts .link_button, #search_actions_search .link_button, #tagged_actions_display .link_button, #discover_actions_discover .link_button { 
    background: rgb(60,60,100) !important;
    border: 1px solid #eaeaff !important;
    }
/* preserve text color for link posts indash, and text fields in popover blogs */
.indash_blog .link_text a {
    color: inherit !important;
    } 
.post_micro .post_content, .post_micro .post_content a {
    color: #444 !important;
    }
/* color correction for reblog button in post headers */
a.reblog_follow_button:not(.icon_follow_rounded_small):before, .reblog_follow_button i {
    color: #fff !important;
    background-color: #000020 !important; 
    }
}
