background: url('http://img100.imageshack.us/img100/1418/bbicons.png') center no-repeat !important;







background-color:#fff !important;







}







img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{







    width:0;







    height:70px;







    padding-right:250px;







    opacity: 0;







