    background: url('http://media.tumblr.com/096ba58ba3c5ee660e896b1e9116c6ca/tumblr_inline_ngc7ifhlYZ1qkv2pp.png') !important;}  
.tab_bar .tab_activity .tab_anchor {background-position: -105 0}  
