css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://namakoteam.com/linklog/") {
body {
  background-color: black;
  color: white;
}
#pagetitle {
  position: absolute;
  top: .3em; left: .3em;
  font-size: 3em;
  font-family: "Lucida Sans", sans-serif;
}
#rss {
  position: absolute;
  left: .4em; top: 4.2em;
}
#toptags {
  position: absolute;
  width: 55%;
  right: 1em; top: 1em;
  line-height: 1.7em;
  text-align: right;
}
#toptags a {
  padding: .2em;
}
a {
  /*background-color: #004E93;*/
  background-color: navy;
  color: white !important;
}
a:hover {
  /*background-color: #337FC6;*/
  background-color: brown;
}
.entrybox {
  position: absolute;
  top: 7em; left: 1em;
  width: auto;
  background-color: transparent !important;
}
.dateheading {
  color: gray;
  margin: 2em 0 0 0;
}
.entry {
  position: relative;
  width: auto;
  margin: 0;
  background-color: transparent !important;
  height: 2.3em;
  overflow: hidden;
}
.taglist {
  display: none;
}
.description {
  position: relative;
  font: bold 2em/1.4em Arial, sans-serif;
  background-color: transparent;
}
a {position: relative; float: none;}
.nav {
  font: bold 1.5em Arial, sans-serif;
  margin: 0 1em 0 0;
}
.footer {
  margin: 0 0 0 .5em; padding: 3em 0 1em 0;
  color: gray;
}
}
