css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://flickr.com/photos/), url-prefix(http://www.flickr.com/photos/) {

 .CollectionsColumn, .contextThumbsRow, .context_crap_td,
 .nextprev_contextThumbsDiv, .contextThumbsMoverWrapperDiv,
 .contextThumbsMoverDiv, .nextprev_contextThumbsArrowsDiv,
 .contextThumbLink, .nextprev_arrows_img_prev, .nextprev_arrows_img_next,
 .contextInfo, .contextInfoInner, .photosNum, .photoText, .showLink
  {
    background-color: black !important;
    border-top-color: black !important;
    border-right-color: black !important;
    border-bottom-color: black !important;
    border-left-color: black !important;
  }

  BODY, TABLE#TopBar, TABLE.NavBar, DIV.candy_menu, DIV#Main, DIV.TagList, DIV#button_bar, DIV.contextCrumbsDiv  {
    background-color: black !important;
    border-top-color: black !important;
    border-right-color: black !important;
    border-bottom-color: black !important;
    border-left-color: black !important;
  }

  DIV.contextCrumbsDivInner A:link, TD.Status a:link, TD.Status a:visited {
    color: white !important;
  }

  DIV.photoImgDiv {
    margin-top: 60px !important;
    margin-right: 60px !important;
    margin-bottom: 60px !important;
    margin-left: 60px !important;
  }

  TABLE#TopBar, TABLE.NavBar, UL.site_nav_menu_buttons, LI.menu_li {
    border-top-width: 0px !important;
    border-right-width: 0px !important;
    border-bottom-width: 0px !important;
    border-left-width: 0px !important;
    margin-top: 0px !important;
    margin-right: 0px !important;
    margin-bottom: 0px !important;
    margin-left: 0px !important;
    padding-top: 0px !important;
    padding-right: 0px !important;
    padding-bottom: 0px !important;
    padding-left: 0px !important;
    text-decoration: none !important;
  }

  #Topbar IMG, .Privacy IMG, .Pro, .ProIcon, #button_bar, .Who IMG, .Said IMG, TD.RHS {
    -moz-opacity: 0.7 !important;
    opacity: 0.7 !important;
  }
  #Topbar INPUT, .Status {
    -moz-opacity: 0.7 !important;
    opacity: 0.7 !important;
  }
  #Photo IMG {
    -moz-opacity: 1 !important;
    opacity: 1 !important;
  }
  A, H1, H2, H3, H4, #other_contexts_p {
    color: #555555 !important;
    text-decoration: none !important;
  }
  .Said, .photoDescription {
    color: #BBBBBB !important;
    text-decoration: none !important;
  }
  #btn_preview_comment, #btn_post_comment {
    background-color: #888888 !important;
  }

/* New 2008-12-1: I've copied and pasted the enhancements suggested by user
Matvey in comments on this style which clean up loose ends in my quick hack.
I still need to minimize all this style's CSS code to the strict minimum
necessary. */

a{color:#555 !important;text-decoration:none !important;}
a:hover{color:#ddd !important;}
div.topBar{background-image:none !important;border-bottom:solid 1px #444;}
div.desc{color:#484848;}
.menu_li, .menu_li span, .no_menu_li, .no_menu_li span{border:none !important;}
.no_menu_li a{color:#999 !important;}
#SSButtonHugger a{border:none !important;}

h3.contextTitleClosed, h3.contextTitleOpen{border:none !important;}
.Paginator span, .Paginator a{background-color:#000 !important;}
.Paginator a{border-color:#333 !important;}
.Footer{background-image:none !important;border-top:solid 1px #444 !important;}

}
