css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/blog") {

#dashboard_controls a, .control, #posts .post.new_post .short_post_labels .new_post_label {
    color: #000000 !important;
}

#auto_pagination_loader {
    background: #e37c00 !important;
}

#next_page_link {
    background-color: #e37c00 !important;
}

.notification .avatar_frame {
    border: none !important;
}

.small_links a {
    color: #000000 !important;
}

.subheading {
    color: #000000 !important;
}

small a {
    color: #000000 !important;
}

.notification blockquote{
    border-left: 3px solid #000000 !important;
}

.hide_overflow.has_follow_button small {
    color: #000000 !important;
}

a#open_blog_link {
    color: #000000 !important;
}

.notification {
    background-color: #ffa300 !important;
    border: 1px solid #ffa300 !important;
    color: #000000 !important;
}

.notification a {
    color: #000000 !important;
    border-bottom: none !important;
}

.nipple {
    border-right: 6px solid #ffa300 !important;
}

.permalink {
    background: url('http://i45.tinypic.com/2dbv6ah.jpg') !important;
}

#dashboard_index {
    background: #ffa00f !important;
}

.selection_nipple {
    border-bottom: 12px solid #e37c00 !important;
}

#dashboard_index #container #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    background-color: #e37c00 !important;
}

.tab_notice {
    background-color: #ffa300 !important;
    color: #000000 !important;
    font-family: Verdana !important;
}

.tab_notice_nipple {
    background-image: url('http://i50.tinypic.com/2vx4xuu.jpg') !important;
}
}
