css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document  url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')  {
body {
    background: url('http://1.bp.blogspot.com/-hX9EdMhqL1Q/Tf8OTJ7fmLI/AAAAAAAAABs/BgDiCYbvne0/s0/CheeseCraft2.jpg') center center #000000 no-repeat fixed !important;
  }
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(0,0,0,0.1);
padding: 20px;
-webkit-border-radius: 15px;
-moz-border-radius: 15px;
border-radius: 15px;
}

div.l-header #search_query {
padding: 9px 40px 9px 9px;
background: url(https://secure.assets.tumblr.com/images/dashboard_controls/dashboard_controls_sprite.png) 191px -542px no-repeat rgba(0, 0, 0, 0.6);
border-width: 0px;
margin: 5px 0px 0px;
border-radius: 28px;
}

div.l-header .selection_nipple {
width: 0;
height: 0;
border-left: 12px solid transparent;
border-right: 12px solid transparent;
border-bottom: 12px solid #2C4762;
opacity:1.0;
font-size: 0;
position: relative;
top: 4px;
vertical-align: top;
margin: 0 auto;
display: none;
}

#right_column .controls_section .recessed {
background-color: #2C4762;
opacity:1.0;
-webkit-box-shadow: none;
-moz-box-shadow: none;
box-shadow: none;
-webkit-border-bottom-right-radius: 6px;
-webkit-border-bottom-left-radius: 6px;
-moz-border-radius-bottomright: 6px;
-moz-border-radius-bottomleft: 6px;
border-bottom-right-radius: 6px;
border-bottom-left-radius: 6px;
position: relative;

}

#right_column .controls_section .recessed:hover {
background-color: #2C4762;
opacity:1.0;
-webkit-box-shadow: 0 0 5px #000;
-moz-box-shadow: 0 0 5px #000;
box-shadow: 0 0 5px #000;
}

#right_column .controls_section .selected {
background: #000;
-webkit-box-shadow: 0 0 5px #000,inset 0 1px 0 rgba(255,255,255,.40);
-moz-box-shadow: 0 0 5px #000,inset 0 1px 0 rgba(255,255,255,.40);
box-shadow: 0 0 5px #000,inset 0 1px 0 rgba(255,255,255,.40);
-webkit-border-radius: 1px;
-moz-border-radius: 1px;
border-radius: 1px;
border-top: none;
}

#right_column .controls_section .selected:hover {
background: rgba(255,255,255,0.1);
-webkit-box-shadow: 0 0 5px #c31c16;
-moz-box-shadow: 0 0 5px #c31c16;
box-shadow: 0 0 5px #c31c16;

border-top: none;
}

#right_column .radar .content {
background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png') repeat-y;
position: relative;
}

#right_column .radar .content.is_media .mask.top {
height: 9px;
background: none;
background-position: 0 0;
top: 0;
}

#right_column .radar .content.is_media .mask.bottom {
height: 19px;
background: none;
background-position: 0 -9px;
bottom: 0;
}

#posts .notification.single_notification {
clear: both;
border: solid 0px #2A3F56;
margin-top: 20px;
-webkit-border-radius: 6px;
-moz-border-radius: 6px;
border-radius: 6px;
}

#posts .notification.alt {
background-color: rgba(0, 0, 0, .45);
}

#posts .notification {
width: 464px;
color: rgba(255, 255, 255, .45);
font-family: "Lucida Grande","Helvetica Neue",Helvetica,Arial,sans-serif;
font-size: 11px;
line-height: 19px;
background-color: rgba(0, 0, 0, .45);
border: solid 0px #2A3F56;
border-left: solid 0px #2A3F56;
border-right: solid 0px #2A3F56;
border-bottom: solid 0px #2A3F56;
-webkit-background-clip: padding-box;
margin: 0 0 0 -1px;
padding: 7px 65px 7px 11px;
position: relative;
}

#posts .post .permalink {
width: 23px;
height: 23px;
background: rgba(0, 0, 0, .45);
position: absolute;
top: -4px;
right: -5px;
display: none;
}

img#content_top, img#content_bottom {
display:none;
}}
