css:
@-moz-document url(http://www.tumblr.com/), url-prefix(http://www.tumblr.com/), domain(www.tumblr.com) , url("https://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {

    #home_button.tab.iconic > a {
      width: 36px !important;
      height: 36px !important;
      background-image: url("http://i.imgur.com/0VqYQ.png?1") !important;
      background-size: 36px 36px !important;
      margin-left: 5px !important;
      opacity: .65 !important;
    }
    
    /*#home_button.tab.iconic > a:hover {
      opacity: .75 !important;
    }*/
  
    .tab_home .tab_anchor:before {
      display: none;
    }
    
    #home_button.tab.iconic.selected > a,
    #home_button.tab.iconic.selected > a:hover  {
      opacity: 1 !important;
    }

}
