@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* ::::: SMALL ::::: */

/* ::::: Redesign Stop Button ::::: */

#nav-bar[iconsize="small"] #stop-button,
#nav-bar[iconsize="small"] #stop-button:not([disabled="true"]):hover,
#nav-bar[iconsize="small"] #stop-button[disabled="true"],
#nav-bar[iconsize="small"] #stop-button:not([disabled="true"]):hover:active {

  width: auto !important;
  padding: 0px !important;
  margin: 0px !important;
  margin-right: 1px !important;
  -moz-appearance: none !important;
  border: none !important;

}

#nav-bar[iconsize="small"] #stop-button > .toolbarbutton-icon {

  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABcAAAAUCAYAAABmvqYOAAAAB3RJTUUH2AUVCBgpUyRNsgAAAAlwSFlzAAALEwAACxMBAJqcGAAAAARnQU1BAACxjwv8YQUAAAAsSURBVHjaY2CgIWD8////fhqZ7chES5ePGj5q+Kjho4aPGj48DAfVRDQzHAACpwcfsQXmCgAAAABJRU5ErkJggg==") no-repeat center center !important;
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: auto !important;
  margin:0px !important;
  padding: 0px !important;
  border-top: 2px solid !important;
  border-bottom: 2px solid !important;
  border-right: 2px solid !important;
  -moz-border-radius-topright: 3px !important;
  -moz-border-radius-bottomright: 3px !important;
  -moz-border-top-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.7) !important;
  -moz-border-right-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) !important;
  -moz-border-bottom-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) !important;
}

#nav-bar[iconsize="small"] #stop-button:not([disabled="true"]):hover > .toolbarbutton-icon {
  list-style-image: url("data:image/png;base64,") !important;
}


#nav-bar[iconsize="small"] #stop-button:not([disabled="true"]):hover:active > .toolbarbutton-icon {
  list-style-image: url("data:image/png;base64,") !important;
}


/* ::::: Redesign Reload Button ::::: */

#nav-bar[iconsize="small"] #reload-button,
#nav-bar[iconsize="small"] #reload-button:not([disabled="true"]):hover,
#nav-bar[iconsize="small"] #reload-button[disabled="true"],
#nav-bar[iconsize="small"] #reload-button:not([disabled="true"]):hover:active {

  width: auto !important;
  padding: 0px !important;
  margin: 0px !important;
  margin-left: -4px !important;
  -moz-appearance: none !important;
  border: none !important;

}

#nav-bar[iconsize="small"] #reload-button > .toolbarbutton-icon {

  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABgAAAAUCAYAAACXtf2DAAAAB3RJTUUH2AUVCBc7JwUgNQAAAAlwSFlzAAALEwAACxMBAJqcGAAAAARnQU1BAACxjwv8YQUAAAAsSURBVHjaY2CgMWD8////flpawERrH4xaMGrBqAWjFoxaMGoBCIBqNJpaAACHAQbePI7ZsAAAAABJRU5ErkJggg==") no-repeat center center !important;	
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: auto !important;
  margin:0px !important;
  padding: 0px !important;
  border-top: 2px solid !important;
  border-bottom: 2px solid !important;
  -moz-border-top-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.7) !important;
  -moz-border-bottom-colors: rgba(255, 255, 255, 0.5) rgba(50, 50, 50, 0.3) !important;
}

#nav-bar[iconsize="small"] #reload-button:not([disabled="true"]):hover > .toolbarbutton-icon {
  list-style-image: url("data:image/png;base64,") !important;
}


#nav-bar[iconsize="small"] #reload-button:not([disabled="true"]):hover:active > .toolbarbutton-icon {
  list-style-image: url("data:image/png;base64,") !important;
}


/* ::::: Highlight Stop and Reload Button on urlbar hover ::::: */

#nav-bar[iconsize="small"] #urlbar-container:hover + #reload-button > .toolbarbutton-icon {
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABgAAAAUCAYAAACXtf2DAAAAB3RJTUUH2AUVCBgVfEsxNQAAAAlwSFlzAAALEwAACxMBAJqcGAAAAARnQU1BAACxjwv8YQUAAAAnSURBVHjaY2CgMWD8////U1pawERrH4xaMGrBqAWjFoxaMGoBXQAAe7cEBr3gVkUAAAAASUVORK5CYII=") no-repeat center center !important;
}

#nav-bar[iconsize="small"] #urlbar-container:hover + #reload-button + #stop-button > .toolbarbutton-icon {
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABcAAAAUCAYAAABmvqYOAAAAB3RJTUUH2AUVCBYmXRh9rQAAAAlwSFlzAAALEwAACxMBAJqcGAAAAARnQU1BAACxjwv8YQUAAAArSURBVHjaY2CgIWD8////U5oYzMgozURLl48aPmr4qOGjho8aPjwMpykAABMDBCTXayFoAAAAAElFTkSuQmCC") no-repeat center center !important;
}


/* ::::: Redesign Urlbar Dropmarker ::::: */

#nav-bar[iconsize="small"] #urlbar{
  border-right: none !important;
  -moz-border-radius-topright: 0px !important;
  -moz-border-radius-bottomright: 0px !important;
}

#nav-bar[iconsize="small"] #urlbar .dropmarker-icon {
  display:none !important;
}

#nav-bar[iconsize="small"] #urlbar > dropmarker {
  width: 19px !important;
  margin-top: -1px !important;
  margin-bottom: -1px !important;
}

#nav-bar[iconsize="small"] #urlbar > dropmarker {
  background: url("data:image/png;base64,") no-repeat center center !important;
}

#nav-bar[iconsize="small"] #urlbar[focused="true"] > dropmarker {
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABIAAAASCAYAAABWzo5XAAAAB3RJTUUH2AUVCBk2xzdxBgAAAAlwSFlzAAALEAAACxABrSO9dQAAAARnQU1BAACxjwv8YQUAAABjSURBVHjaY2AYBaMAAe48etHw8fO3/7jwvSevGrDpY0QXACnm50/DadHnz7MZeLk5MfQxoQu8/fC5EaQYlyGv339uJNp7IOd//vr9PwNTDByD+Li8RbRhZBuCbhhFhowC6gMAKm1fuIv++6EAAAAASUVORK5CYII=") no-repeat center center !important;
}

#nav-bar[iconsize="small"] #urlbar[level="high"] > dropmarker {
  background: url("data:image/png;base64,") no-repeat center center !important;
  border-left: 1px solid !important;
  -moz-border-left-colors: rgba(245, 246, 190, 0.65) !important;
}

#nav-bar[iconsize="small"] #urlbar[level="high"][focused="true"] > dropmarker {
  background: url("data:image/png;base64,") no-repeat center center !important;
  border-left: 1px solid !important;
  -moz-border-left-colors: rgba(245, 246, 190, 0.99) !important;
}

#nav-bar[iconsize="small"] #urlbar > dropmarker:hover,
#nav-bar[iconsize="small"] #urlbar[level="high"] > dropmarker:hover {
  background: url("data:image/png;base64,") no-repeat center center !important;
  -moz-image-region: auto !important;
  border-left: 1px solid !important;
  -moz-border-left-colors: #000 !important;
}

#nav-bar[iconsize="small"] #urlbar > dropmarker:hover:active,
#nav-bar[iconsize="small"] #urlbar[level="high"] > dropmarker:hover:active {
  background: url("data:image/png;base64,") no-repeat center center !important;
  -moz-image-region: auto !important;
  border-left: 1px solid !important;
  -moz-border-left-colors: #000 !important;
}

#nav-bar[iconsize="small"] .textbox-icon-box {
  margin-top: -1px !important;
  margin-bottom: -1px !important;
  margin-left: -1px !important;
}


/* ::::: Fix Searchbar ::::: */

#nav-bar[iconsize="small"] .searchbar-textbox {
  height: 24px !important;
  margin-right: 0px !important;
}

#nav-bar[iconsize="small"] .search-go-button {
  margin-right: 12px !important;
}

#nav-bar[iconsize="small"] #search-container {
  -moz-box-flex: 0 !important;
  max-width: 250px !important;
  min-width: 250px !important;
}

#nav-bar[iconsize="small"] .search-go-button {
  padding: 0px !important;
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: auto !important;
  margin-top: -1px !important;
  margin-bottom: -1px !important;
}

#nav-bar[iconsize="small"] .search-go-button:hover {
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: auto !important;
  margin-top: -1px !important;
  margin-bottom: -1px !important;
  padding: 0px !important;

}

#nav-bar[iconsize="small"] .search-go-button:hover:active {
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: auto !important;
  margin-top: -1px !important;
  margin-bottom: -1px !important;
  padding: 0px !important;

}

#nav-bar[iconsize="small"] .searchbar-engine-button {
  margin-left: 224px !important;
  margin-right: -244px !important;
  background: none !important;
  width: 19px !important;
  height: 22px !important;
  border-right: none !important;
  margin-top: -1px !important;
  margin-bottom: -1px !important;
}

#nav-bar[iconsize="small"] .searchbar-engine-image { display: none !important;}

#nav-bar[iconsize="small"] .searchbar-dropmarker-image {
  list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAUCAYAAAC9BQwsAAAAB3RJTUUH2AUWCjIIYRfqRAAAAAlwSFlzAAALEAAACxABrSO9dQAAAARnQU1BAACxjwv8YQUAAABtSURBVHjaY2AYBcMBMCJz7jx68V9UkA/M5uZih4t//fYTTL/9+IVBSUYMrIcFWSNIEz9/Gk5bPn+eDWczIUu8/fAZRRJd0+v3n3G7/d6TV/8/f/3+n4EpBo5BfJA4QY8jayZaE7pmkjSNAgoBAOxoRqxwcW8pAAAAAElFTkSuQmCC") !important;
  border: none !important;
}

#nav-bar[iconsize="small"] .searchbar-engine-button:hover .searchbar-dropmarker-image {
  list-style-image: url("data:image/png;base64,") !important;
  margin-top: -1px !important;
  margin-bottom: -1px !important;
}

#nav-bar[iconsize="small"] .searchbar-engine-button:hover:active .searchbar-dropmarker-image {
  list-style-image: url("data:image/png;base64,") !important;
}

#nav-bar[iconsize="small"] .searchbar-engine-button[addengines="true"] > .button-box {
  background: none !important;
}
