css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {


body { background: #fff url(http://24.media.tumblr.com/tumblr_ly0ae2N4hE1r9g6hvo7_r1_250.png) repeat fixed; }


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content { background-color:transparent!important; }


*{ text-shadow:none !important; color:#676767 !important; }


ol#posts li.notification {
    background-color: #ceecf4 !important;
    border-bottom: 0px solid #9d92a5 !important;
    color: #FFFFFF !important;
}
  

a.sidebar_link { display: none; }


div#right_column a#dashboard_switch_blog_menu_current:hover { background:#FFFFFF; display: none!important; }
    
    
#nav .nav_item.active{ background-color:#FFFFFF; }
    

#left_column #posts .post .permalink{background-color:#FFFFFF;}


#dashboard_switch_blog_menu_current, #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current { background-image:none !important; }


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
      background: none !important;
      width: 0px !important;
      height: 0px !important;
      margin-top: 0px !important;
      border-bottom: solid 11px white !important;
      border-right: 11px solid transparent !important;
      border-left: 11px solid transparent !important;
}
          

.selection_nipple:not([class="selection_nipple white"]) { border-bottom: solid 11px transparent !important; }

        
.notification_type_icon{ display:none !important; }


.selected .following, .selected .followers, .selected .settings, .selected .posts, .selected .messages, .selected .drafts, .selected .queue, .selected .likes { 
    color: #A099A1 !important; 
	background: #FFFFFF !important;
}


a.explore_more_tags, a.add_and_remove, a.customize, a.mass_editor {
	background-color:#FFFFFF !important;  
	background-image:none !important;
	border:0px !important;
}


div.gradient, div.dashboard_nav_item, a.permalink {
	background-color:#CCC !important;
    opacity:.9; 
    background-image:none !important;
    border:0px !important;
}


.content.is_media, .mask.top, .mask.bottom { background: none !important; }


.post.photo_post .photo, .post.photo_post .video_thumbnail { border-radius: 16px !important; }


.post.photo_post .photo, .post.photo_post .video_thumbnail { -moz-box-shadow: 1px 1px 1px #000000 !important; }


#auto_pagination_loader { background: #A099A1 !important; }


div.return_to_top_icon { 
    background: none !important;
    width: 0px !important;
    height: 0px !important;
    margin-top: 0px !important;
    border-bottom: solid 30px white !important;
    border-right: 15px solid transparent !important;
    border-left: 15px solid transparent !important;
}


#left_column #posts .notification {
    background-color:#FFFFFF !important;
    color:#666 !important;
    border: 1px solid #eee!important;
}

.nipple { background-color:#FFF; }


.nipple.border { border: 1px solid #eee!important; }


.notification_avatar { border: 1px solid #eee; }


#new_post_notice_container { color:#fff !important; }


#new_post_notice_container a { color:#fff !important; }


.permalink {
    background: none !important;
    width: 16px !important;
    height: 16px !important;
    background: #FFFFFF!important;
    border-bottom-left-radius: 10px !important;
    border-top-right-radius: 10px !important;
    margin-top: 3px !important;
    margin-right: 4px !important;
}

.search_form_row { 
  background: #eee;
  opacity: 0.5;
  color: #fff;
}

.search_query_submit { -webkit-filter: invert(100%); }
  
.right_column:after { display: none; }
      
.controls_section.user_list li .follow_list_item_blog:before {
  background: transparent;
  border: 0px solid #fff;
}

ul.sidebar_nav.clearfix { display: none; }

}
