css:
@namespace url(http://www.w3.org/1999/xhtml);

/* v 1.2b
 *   
 * Author	: ceek (ceek@163.com)
 * Updated	: http://userstyles.org/styles/7428 ,May 16, 2008
 * Description	: A style redesign of the appearance and feel for tianya.cn bbs
 * Usage	: Use with the Stylish Firefox extension 
 * 
 * 
 */


@-moz-document domain("tianya.cn") {
body,div,a,select,input,textarea,td,p,.lb12{font-family:microsoft yahei,Tohoma,Airal,sans-serif !important;/*color:#333*/ !important}
a:link{color:blue !important; text-decoration: underline !important}
a:visited{color:grey  !important; text-decoration: underline !important}
a:hover{color:red !important;text-decoration: underline !important}
u,u:link,u:visited{color:#C0C0C0 !important;text-decoration:underline !important }
table[valign=top]{display:none !important}
}


@-moz-document url-prefix(http://www.tianya.cn/new) ,url-prefix(http://www.tianya.cn/New),url-prefix(http://www.tianya.cn/publicforum/articleslist), url-prefix(http://www.tianya.cn/techforum/articleslist), url-prefix(http://bbs.city.tianya.cn/new),url-prefix(http://www.tianya.cn/Pub){
table{width:100% !important}
a{line-height:22px !important;padding:2px !important;padding-left:0px !important;}
.tdfont{font-size:12px !important;color:#666666 !important;text-align:center !important}
td[width="40"] {text-align:center !important}
div#marqueeBox,table[bgcolor="#ecf1f7"],table[height="80"],td[class="font12"],iframe{display:none !important}
.lb12{font-size:14px !important}
a.banzhu{color:#369 !important}
font[color="#cc00cc"]{color:#3B5998 !important}
font[color="red"][size="2"]{color:#3B5998 !important;font-weight:normal !important}
font[color="red"]{color:#CC0000 !important}
td[bgcolor="#6699cc"] font[color="red"]{color:white !important}
div[id="Layer1"]{display:none !important}
}


@-moz-document url-prefix(http://www.tianya.cn/leftlist) {
a{padding-left:4px !important}
a:link,a:visited,a:active{ color:#FFFFFF !important;text-decoration:none !important;}
a:hover{ color:red !important ; text-decoration:none !important ;background:none !important }
span,font,TD{color:#FFFFFF !important}
}


@-moz-document url(http://www.tianya.cn/Refresh_new.shtml),url(http://www.tianya.cn/Refreshnew.html), url(http://ref.bbs.tianya.cn/refreshnew.htm){
a:link,a:visited,a:hover,body,font,.online{color:#FFFFCC !important;font-size:12px !important  }
}


@-moz-document url-prefix(http://www.tianya.cn/publicforum/content),url-prefix(http://www.tianya.cn/techforum/content),url-prefix(http://www.tianya.cn/pub) {
iframe,#tianyatopguide_nav,img#googleLogo,embed,div[id="randPanel"],span[id="tianyaSense1"]{display:none !important}
.content embed{display:block !important}
td embed[width="680"]{display:none !important}
#pageDiv,#pageDiv1{height:22px !important;width:400px !important}
textarea{color:#3366FF !important;width:760px !important}
center{padding-bottom:2px !important}
div#DisplayLoginInfoDiv font[color="red"]{/*color:#3399FF !important;*/padding-left:6px !important}
/*clear bottom post tip*/.tianyatopguidenavlogo,.tianyatopguidenavl,.tianyatopguidenavr,.friend,.explain{display:none !important}
/*clear bottom copy annoucement*/#tianyafootercopyArea{display:none !important}
}


@-moz-document url-prefix(http://search.tianya.cn) {
.t_list,iframe{display:none !important}
}


@-moz-document url-prefix(http://focus.tianya.cn) {
.banner,iframe,#FOCUS_ADRIGHT_1{display:none !important}
a:hover{ text-decoration: none !important;padding-top:2px !important;padding-bottom:2px !important}
}
