css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

.tab-stack {
	min-height: 29px !important}
.tabbrowser-tab {
	-moz-appearance: none !important;
	padding: 0 !important;
	border: 0 solid Transparent !important;
	background: 0 !important;
	-moz-border-image: none !important}
.tab-throbber[pinned],
.tab-icon-image[pinned] {
	-moz-margin-start: 4px !important;
	-moz-margin-end: 3px !important}
.tab-content {
	border: solid Transparent !important;
	border-width: 0 !important;
	padding: 4px 24px 4px 22px !important;
	margin-left: -30px !important}
.tabbrowser-tab[pinned] .tab-content {
	padding: 4px 22px 4px !important;}
#tabbrowser-tabs > tab:first-child .tab-content {
	margin-left: -30px !important}
.tabbrowser-arrowscrollbox > .arrowscrollbox-scrollbox {
	-moz-padding-start: 25px !important;
	-moz-padding-end: 0 !important}
.tabbrowser-tab[selected="true"] > .tab-stack {
	z-index: 1 !important;
	position: relative !important}
.tabbrowser-tab:-moz-lwtheme {
	color: inherit !important;
	text-shadow: inherit !important}
.tabs-newtab-button,
#TabsToolbar > #new-tab-button,
#TabsToolbar > toolbarpaletteitem > #new-tab-button {
	margin-bottom: -1px !important}
.tabs-newtab-button,
#TabsToolbar #home-button {
	-moz-margin-start: -30px !important;
	min-width: 64px !important;
	padding: 0 !important;
	padding-top: 1px !important;
	margin-top: -1px !important;
	border: 0 solid Transparent !important;
	background: 0 !important}
.tabs-newtab-button,
#TabsToolbar #home-button,
.tabbrowser-tab:not(:first-child) > .tab-stack > .tab-content:not([selected="true"]) {
	background-image: url("data:image/png;base64,") !important;
	background-repeat: no-repeat !important;
	background-position: 6px !important}
#TabsToolbar #home-button {
	-moz-margin-start: -4px !important;
	-moz-margin-end: -24px !important;
	background-position: -moz-calc(100% + 2px) !important}
.tabbrowser-tab[pinned][titlechanged] > .tab-stack > .tab-content:not([selected="true"]) {
	background-image: url("data:image/png;base64,") !important;
	background-repeat: no-repeat !important;
	background-position: -moz-calc(50% + 1px) 2px !important;
	background-size: auto !important}
.tabbrowser-tab:not(:first-child)[pinned][titlechanged] > .tab-stack > .tab-content:not([selected="true"]) {
	background-image: url("data:image/png;base64,"),
					  url("data:image/png;base64,") !important;
	background-repeat: no-repeat !important;
	background-position: -moz-calc(50% + 1px) 2px, 6px !important;
	background-size: auto, auto !important}
.tabbrowser-tab[pinned][titlechanged] > .tab-stack > .tab-content:not([selected="true"]):hover {
	background-image: url("data:image/png;base64,"),
					  url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAfCAYAAADEFbwbAAAAP0lEQVQokWPhkjfmYsACWIBYGavEy2tH9bFKcLKzPMYqwcjI+B+XHTgl/g2kDppLAIOEinb8pZ5RA+rzAUsMAOzWH4mRgtJTAAAAAElFTkSuQmCC"),
					  url("data:image/png;base64,"),
					  url("data:image/png;base64,") !important;
	background-repeat: no-repeat !important;
	background-position: left, 30px, right, -moz-calc(50% + 1px) 2px !important;
	background-size: 30px 100%, -moz-calc(100% - 60px) 100%, 30px 100%, auto !important}
.tabs-newtab-button:hover,
#TabsToolbar #home-button:hover,
.tabbrowser-tab > .tab-stack > .tab-content:not([selected="true"]):hover,
.tabbrowser-tab:-moz-lwtheme > .tab-stack > .tab-content[selected="true"] {
	background-image: url("data:image/png;base64,"),
					  url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAfCAYAAADEFbwbAAAAP0lEQVQokWPhkjfmYsACWIBYGavEy2tH9bFKcLKzPMYqwcjI+B+XHTgl/g2kDppLAIOEinb8pZ5RA+rzAUsMAOzWH4mRgtJTAAAAAElFTkSuQmCC"),
					  url("data:image/png;base64,") !important;
	background-repeat: no-repeat !important;
	background-position: left, 30px, right !important;
	background-size: 30px 100%, -moz-calc(100% - 60px) 100%, 30px 100% !important}
.tabbrowser-tab > .tab-stack > .tab-content[selected="true"] {
	position: relative !important;
	background-image: url("data:image/svg+xml;base64,"),
					  url("data:image/svg+xml;base64,"),
					  url("data:image/svg+xml;base64,") !important;
	background-repeat: no-repeat !important;
	background-position: left 1px, 30px 1px, right 1px !important;
	background-size: 30px 100%, -moz-calc(100% - 60px) 100%, 30px 100% !important}
.tabbrowser-tab:not(:last-child):not([selected="true"]):hover + .tabbrowser-tab:not([selected="true"]) > .tab-stack > .tab-content:-moz-locale-dir(ltr),
.tabbrowser-tab:not(:last-child)[selected="true"] + .tabbrowser-tab:not([selected="true"]):not(:hover) > .tab-stack > .tab-content:-moz-locale-dir(ltr) {
	background-image: none !important}
.tabbrowser-tab:not(:last-child):not([selected="true"]):hover + .tabbrowser-tab[pinned][titlechanged]:not([selected="true"]) > .tab-stack > .tab-content:-moz-locale-dir(ltr),
.tabbrowser-tab:not(:last-child)[pinned][selected="true"] + .tabbrowser-tab[pinned][titlechanged]:not([selected="true"]):not(:hover) > .tab-stack > .tab-content:-moz-locale-dir(ltr) {
	background-image: url("data:image/png;base64,") !important;
	background-repeat: no-repeat !important;
	background-position: -moz-calc(50% + 1px) 2px !important;
	background-size: auto !important}

.tab-drop-indicator {
	padding-right: 30px !important}
.tabbrowser-tab:not([selected]) .tab-icon-image {
	list-style-image: url("data:image/png;base64,") !important}

.tabbrowser-tab:not([pinned])[fadein] {
	max-width: 222px !important}

.tabs-newtab-button,
#TabsToolbar > #new-tab-button,
#TabsToolbar > toolbarpaletteitem > #new-tab-button {
	margin-bottom: -1px !important;
	list-style-image: url("data:image/svg+xml;base64,") !important}
