css:
@-moz-document domain(forbes.com) {

/* Ads and sharing */

#leaderboardad, .top_link_strip, aside .block.ad { 
	display: none !important;
}
#follow_bar, .sponsorship, .top_banner { 
	display: none !important;
}
.article_lower.ad, .box.article, .blockAd, #sidebar_follower .shareholder { 
	display: none !important;
}
#sticky_sharing.sticky_sharing { 
	display: none !important;
}
#sidebar_follower .shareholder {
	display: none !important;
}

/* Body and Articles */

.article_head {margin-top: 20px;
}

#promoblock h2 {
	font: 40px/44px Georgia !important;
}
.article .body p { 
	text-indent: 15px; 
	font-family: Helvetica !important; 
	color: #333 !important; 
	font-size: 17px !important; 
	line-height: 28px !important;
}
.article .body p:first-letter { 
	font-size: 22px; 
	font-family: Verdana; 
	margin-right: 1px;
}
#contentwrapper {
	margin: 0 auto !important;
    width: 980px !important;
}
.article_head h1 {
	font-size: 32px !important;
    color: #333 !important;
    font-family: Helvetica !important;
    line-height: 35px !important;
}
.entity_block p.user a {
	color: #3c3c3c
}
.entity_block p.user a:hover { 
	color: grey;
    -webkit-transition: color .25s linear;
    transition: color .25s linear;
}
.entity_block p.user .desc {
	font-style: italic;
}
.article .body .wp-caption p.wp-caption-text, .article .body .wp-caption p.wp-caption-text a { 
	font-size: 12px !important; 
	text-align: center;
}
div.leaderboard.above_header, #leaderboardad, .prWrap {
	display: none;
}
h3 {
	font: 18px/20px Georgia !important;
	color: #333 !important;
}

/* Footer */

.footcontainer { 
	margin: 0 auto;
}
}
